import React, { useEffect, useState, useMemo, useRef } from "react";
import ScheduleView from "./ScheduleView";
import {
  mapRow,
  parseScheduleText,
  buildStatusSections,
  formatDateTime,
} from "./utils";
import "./TruckTable.css";

export default function TruckTable() {
  /* ─────────── ÉTATS PRINCIPAUX ─────────── */
  const [rows, setRows] = useState([]);
  const [error, setError] = useState("");
  const [lastUpdated, setLastUpdated] = useState(null);
  const [scheduleParsed, setScheduleParsed] = useState({ title: "", rows: {} });
  const [index, setIndex] = useState(0);

  /* ─────────── RÉFÉRENCES ─────────── */
  const timeoutRef = useRef(null);
  const pausedRef = useRef(false);

  /* ─────────── CHARGEMENT DES CSV ─────────── */
  useEffect(() => {
    async function loadStatus() {
      try {
        const res = await fetch("/truck_status.csv", { cache: "no-store" });
        const text = await res.text();
        const lm = res.headers.get("Last-Modified");
        setLastUpdated(lm ? new Date(lm) : new Date());

        const clean = text.replace(/^\uFEFF/, "");
        const lines = clean.split(/\r?\n/).filter((l) => l.trim().length > 0);
        if (!lines.length) return;

        const headers = lines[0].split(";").map((h) => h.trim());
        const data = lines.slice(1).map((line) => {
          const cells = line.split(";");
          const obj = {};
          headers.forEach((h, i) => (obj[h] = (cells[i] ?? "").trim()));
          return obj;
        });

        const mapped = data
          .map(mapRow)
          .filter((r) =>
            Object.values(r).some((v) => (v ?? "").toString().trim() !== "")
          );

        setRows(mapped);
      } catch (e) {
        setError("Impossible de charger le CSV statut");
        console.error(e);
      }
    }
    loadStatus();
  }, []);

  useEffect(() => {
    async function loadSchedule() {
      try {
        const res = await fetch("/schedule.csv", { cache: "no-store" });
        if (!res.ok) return;
        const text = await res.text();
        setScheduleParsed(parseScheduleText(text));
      } catch (e) {
        console.error("Impossible de charger schedule.csv", e);
      }
    }
    loadSchedule();
  }, []);

  /* ─────────── CONSTRUCTION DES SECTIONS ─────────── */
  const statusSections = useMemo(() => buildStatusSections(rows), [rows]);
  const hasSchedule =
    scheduleParsed.title.trim().length > 0 ||
    Object.keys(scheduleParsed.rows).length > 0;

  const sections = useMemo(() => {
    const s = [];
    if (statusSections.length) s.push(...statusSections);
    if (hasSchedule)
      s.push({ name: "Planification atelier", type: "schedule", rows: [] });
    return s;
  }, [statusSections, hasSchedule]);

  const current = sections[index] || sections[0] || {
    type: "status",
    rows: [],
  };

  /* ─────────── SLIDESHOW : setTimeout RÉCURSIF ─────────── */
  useEffect(() => {
    if (!sections.length) return;

    let isActive = true;

    const nextSlide = () => {
      if (!isActive || pausedRef.current) return;

      setIndex((i) => (i + 1) % sections.length);

      if (isActive && !pausedRef.current) {
        timeoutRef.current = setTimeout(nextSlide, 15000); // 15 s
      }
    };

    timeoutRef.current = setTimeout(nextSlide, 15000);

    /* cleanup */
    return () => {
      isActive = false;
      clearTimeout(timeoutRef.current);
      pausedRef.current = false;
    };
  }, [sections.length]);

  /* ─────────── GESTION PAUSE SUR SURVOL ─────────── */
  const onMouseEnter = () => {
    pausedRef.current = true;
    clearTimeout(timeoutRef.current);
  };

  const onMouseLeave = () => {
    pausedRef.current = false;
    clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(
      () => setIndex((i) => (i + 1) % sections.length),
      15000
    );
  };

  /* ─────────── NAVIGATION MANUELLE ─────────── */
  const prev = () =>
    setIndex((i) => (i === 0 ? Math.max(0, sections.length - 1) : i - 1));
  const next = () => setIndex((i) => (i + 1) % sections.length);

  /* ─────────── RENDU ─────────── */
  return (
    <div
      className="truck-table-wrapper"
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      {/* Navigation manuelle */}
      <button className="nav prev" onClick={prev}>
        ‹
      </button>
      <button className="nav next" onClick={next}>
        ›
      </button>

      {/* Affichage conditionnel */}
      {current.type === "status" && (
        <table className="truck-table">
          <thead>
            <tr>
              <th>Unité</th>
              <th>DATE</th>
              <th>PLAINTE</th>
              <th>DATE RÉPARATION</th>
              <th>GARAGE</th>
              <th>COMPLÉTÉE</th>
              <th>PROCHAIN RENDEZ-VOUS</th>
              <th>DISPONIBLE</th>
              <th>NOTE</th>
            </tr>
          </thead>
          <tbody>
            {current.rows.map((r, idx) => (
              <tr key={idx} className={r._rowClass}>
                <td>{r.unite}</td>
                <td>{formatDateTime(r.date)}</td>
                <td>{r.plainte}</td>
                <td>{formatDateTime(r.dateReparation)}</td>
                <td>{r.garage}</td>
                <td>{r.completee}</td>
                <td>{formatDateTime(r.prochainRdv)}</td>
                <td>
                  {r.disponible === null
                    ? ""
                    : r.disponible
                    ? "oui"
                    : "non"}
                </td>
                <td>{r.note}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {current.type === "schedule" && (
        <ScheduleView data={scheduleParsed} />
      )}

      {/* Infos de mise à jour */}
      {lastUpdated && (
        <div className="last-updated">
          MAJ : {lastUpdated.toLocaleString()}
        </div>
      )}

      {error && <div className="error">{error}</div>}
    </div>
  );
}
