import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types, setTypes] = useState([]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  useEffect(() => {
    Papa.parse('/truck_status.csv', {
      download: true,
      header: true,
      complete: results => {
        const rows = results.data.map(row => {
          const prefix = row['Unité']?.substring(0, 2).toUpperCase() || 'Inconnu';
          let type;
          if (prefix === 'RO' || prefix === 'CA' || prefix === 'LA') {
            type = prefix;
          } else {
            type = 'Autres';
          }
          row.Type = type;
          return row;
        }).filter(r => r.Type !== 'Inconnu');

        setData(rows);

        const uniqueTypes = ['RO', 'CA', 'LA', 'Autres'];
        setTypes(uniqueTypes);
        setCurrentTypeIndex(0);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex(prev => (prev + 1) % types.length);
    }, 10000);

    return () => clearInterval(interval);
  }, [types]);

  const headers = data.length > 0 ? Object.keys(data[0]).filter(k => k !== 'Type') : [];

  const titleMap = {
    RO: 'Rolloff',
    CA: 'Chargement Avant',
    LA: 'Chargement Latéral',
    Autres: 'Autres Camions',
  };

  const currentType = types[currentTypeIndex] || null;

  const filteredData = data.filter(d => d.Type === currentType);

  return (
    <div style={{ width: '100vw', height: '100vh', overflow: 'auto', padding: '10px' }}>
      {currentType && (
        <h2 style={{ textAlign: 'center', marginBottom: '20px', fontWeight: 'bold', color: '#333' }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: 'collapse', width: '100%', tableLayout: 'fixed' }}>
        <thead>
          <tr>
            {headers.map(h => {
              const isDateRep = h.toUpperCase() === 'DATE RÉPARATION' || h.toUpperCase() === 'DATE REPARATION';
              const isProchaineRDV = h.toUpperCase().includes('PROCHAINE RENDEZ-VOUS') || h.toUpperCase().includes('PROCHAINE RENDEZ VOUS');
              const isCompletee = h.toUpperCase().includes('COMPLÉTÉ') || h.toUpperCase().includes('COMPLETEE');
              const isDisponible = h.toUpperCase() === 'DISPONIBLE';

              return (
                <th
                  key={h}
                  style={{
                    border: '2px solid black',
                    backgroundColor: '#181818',
                    color: 'white',
                    padding: '6px 10px',
                    textTransform: 'uppercase',
                    whiteSpace: 'normal',
                    textAlign: 'center',
                    width:
                      h === 'Unité' ? 115 :
                      h === 'DATE' ? 70 :
                      h === 'Plainte' ? 'auto' :
                      h === 'GARAGE' ? 120 :
                      h === 'Apte à rouler' ? 60 :
                      isDateRep ? 60 :
                      isProchaineRDV ? 60 :
                      isCompletee ? 40 :
                      isDisponible ? 80 :
                      'auto',
                    minWidth:
                      h === 'Unité' ? 115 :
                      h === 'DATE' ? 70 :
                      h === 'Plainte' ? 220 :
                      h === 'GARAGE' ? 80 :
                      h === 'Apte à rouler' ? 60 :
                      isDateRep ? 60 :
                      isProchaineRDV ? 60 :
                      isCompletee ? 40 :
                      isDisponible ? 80 :
                      100,
                    maxWidth:
                      h === 'Plainte' ? 1000 :
                      h === 'GARAGE' ? 150 :
                      h === 'Apte à rouler' ? 60 :
                      isDateRep ? 60 :
                      isProchaineRDV ? 60 :
                      isCompletee ? 40 :
                      isDisponible ? 80 :
                      undefined,
                    wordBreak: isDateRep || isProchaineRDV ? 'break-word' : undefined,
                    lineHeight: isDateRep || isProchaineRDV ? '1.1em' : undefined,
                    paddingTop: isDateRep || isProchaineRDV ? '6px' : undefined,
                    // Pour rendre le texte de la colonne dispo juste centré verticalement aussi
                    verticalAlign: isDisponible ? 'middle' : undefined,
                  }}
                  title={h}
                >
                  {isDateRep ? (
                    <>Date<br />Réparation</>
                  ) : isProchaineRDV ? (
                    <>Prochaine<br />Rendez-vous</>
                  ) : (
                    h
                  )}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers[7];
            const aptVal = row[aptColKey] ? row[aptColKey].toLowerCase() : '';
            const isNon = aptVal === 'non';

            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? '#d32f2f' : idx % 2 === 1 ? '#edeff2' : 'white',
                  color: isNon ? 'white' : 'black',
                }}
              >
                {headers.map((col, ci) => {
                  let val = row[col] || '';
                  let tdStyle = {
                    border: '2px solid black',
                    padding: '8px',
                    overflowWrap: 'break-word',
                    whiteSpace: 'normal',
                    textAlign: 'center',
                    verticalAlign: col.toUpperCase() === 'DISPONIBLE' ? 'middle' : undefined,
                  };

                  if (col === 'Unité') {
                    tdStyle.backgroundColor = '#1565c0';
                    tdStyle.color = 'white';
                    tdStyle.fontWeight = 'bold';
                    tdStyle.width = 115;
                    tdStyle.minWidth = 115;
                    tdStyle.textAlign = 'center';
                  } else if (col === 'DATE') {
                    tdStyle.width = 70;
                    tdStyle.minWidth = 70;
                    tdStyle.textAlign = 'center';
                  } else if (col === 'Plainte') {
                    tdStyle.textAlign = 'left';
                    tdStyle.width = 'auto';
                    tdStyle.minWidth = 220;
                  } else if (col === 'GARAGE') {
                    tdStyle.width = 120;
                    tdStyle.minWidth = 80;
                    tdStyle.maxWidth = 150;
                  } else if (col === 'Apte à rouler') {
                    tdStyle.width = 60;
                    tdStyle.minWidth = 60;
                    tdStyle.maxWidth = 60;
                    tdStyle.textAlign = 'center';
                    if (val.toLowerCase() === 'oui' && !isNon) {
                      tdStyle.backgroundColor = '#43a047';
                      tdStyle.color = 'white';
                      tdStyle.fontWeight = 'bold';
                      tdStyle.borderRadius = '3px';
                      tdStyle.letterSpacing = '1px';
                    }
                  } else if (col.toUpperCase().includes('COMPLÉTÉ') || col.toUpperCase().includes('COMPLETE')) {
                    tdStyle.width = 40;
                    tdStyle.minWidth = 40;
                    tdStyle.maxWidth = 40;
                    tdStyle.textAlign = 'center';
                    tdStyle.fontSize = '0.9em';
                    tdStyle.padding = '2px 5px';
                  } else if (col.toUpperCase().includes('PROCHAINE RENDEZ-VOUS') || col.toUpperCase().includes('PROCHAINE RENDEZ VOUS')) {
                    tdStyle.width = 60;
                    tdStyle.minWidth = 60;
                    tdStyle.maxWidth = 60;
                    tdStyle.textAlign = 'center';
                    tdStyle.wordBreak = 'break-word';
                    tdStyle.lineHeight = '1.1em';
                    tdStyle.paddingTop = '6px';
                  } else if (col.toUpperCase() === 'DISPONIBLE') {
                    tdStyle.width = 80;
                    tdStyle.minWidth = 80;
                    tdStyle.maxWidth = 80;
                    tdStyle.textAlign = 'center';
                    tdStyle.fontSize = '0.9em';
                    tdStyle.padding = '2px 5px';
                    tdStyle.verticalAlign = 'middle';
                  }

                  if (col === aptColKey && val.toLowerCase() === 'oui' && !isNon) {
                    val = val.trim();
                  }

                  if (col.toUpperCase() === 'DATE' && (val.trim() === '' || !val)) val = '-';

                  return <td key={ci} style={tdStyle}>{val}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
