import React, { useEffect, useMemo, useRef, useState } from "react";
import "./TruckTable.css";

/* ====== Helpers statut ====== */
function toBoolOuiNon(val) {
  const v = (val || "").toString().trim().toLowerCase();
  if (v === "oui" || v === "yes" || v === "true") return true;
  if (v === "non" || v === "no" || v === "false") return false;
  return null;
}

function mapRow(obj) {
  return {
    unite: obj["Unité"]?.trim() || "",
    date: obj["DATE"]?.trim() || "",
    plainte: obj["PLAINTE"]?.trim() || "",
    dateReparation: obj["DATE RÉPARATION"]?.trim() || "",
    garage: obj["GARAGE"]?.trim() || "",
    completee: obj["COMPLÉTÉE"]?.trim() || "",
    prochainRdv:
      obj["PROCHAINE DATE DE RÉPARATION"]?.trim() ||
      obj["PROCHAIN RENDEZ-VOUS"]?.trim() ||
      "",
    disponible: toBoolOuiNon(
      obj["DISPONIBLE"] ?? obj["Disponible"] ?? obj["APTE À ROULER"]
    ),
    note: obj["NOTE"]?.trim() || "",
  };
}

function categorize(unite) {
  const u = (unite || "").trim().toUpperCase();
  if (u.startsWith("RO")) return "ROLL-OFF";
  if (u.startsWith("CA")) return "CHARGEMENT AVANT";
  if (u.startsWith("LA")) return "CHARGEMENT LATÉRAL";
  return "Autres camions";
}

function buildStatusSections(rows) {
  const order = [
    "ROLL-OFF",
    "CHARGEMENT AVANT",
    "CHARGEMENT LATÉRAL",
    "Autres camions",
  ];
  const buckets = Object.fromEntries(order.map((k) => [k, []]));
  for (const r of rows) buckets[categorize(r.unite)].push(r);
  return order.map((name) => ({ name, type: "status", rows: buckets[name] }));
}

function formatDateTime(d) {
  try {
    return new Intl.DateTimeFormat("fr-CA", {
      dateStyle: "medium",
      timeStyle: "short",
    }).format(d);
  } catch {
    return d.toLocaleString();
  }
}

/* ====== Vue Statut ====== */
function StatusView({ section }) {
  return (
    <div className="status-section">
      <h3 className="section-title">{section.name}</h3>
      <div className="table-wrapper">
        <table className="truck-table">
          <thead>
            <tr>
              <th>Unité</th>
              <th>DATE</th>
              <th>PLAINTE</th>
              <th>DATE RÉPARATION</th>
              <th>GARAGE</th>
              <th>COMPLÉTÉE</th>
              <th>PROCHAIN RENDEZ-VOUS</th>
              <th>DISPONIBLE</th>
              <th>NOTE</th>
            </tr>
          </thead>
          <tbody>
            {section.rows.map((r, i) => (
              <tr
                key={i}
                className={
                  r.disponible === true
                    ? "status-available"
                    : r.disponible === false
                    ? "status-unavailable"
                    : ""
                }
              >
                <td>{r.unite}</td>
                <td>{r.date}</td>
                <td>{r.plainte}</td>
                <td>{r.dateReparation}</td>
                <td>{r.garage}</td>
                <td>{r.completee}</td>
                <td>{r.prochainRdv}</td>
                <td className="disponible-cell">
                  {r.disponible === null
                    ? ""
                    : r.disponible
                    ? "oui"
                    : "non"}
                </td>
                <td>{r.note}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/* ====== Composant principal TruckTable ====== */
export default function TruckTable({
  csvUrl = "/camions.csv",
  refreshInterval = 30000,
}) {
  const [csvData, setCsvData] = useState([]);
  const [currentSectionIndex, setCurrentSectionIndex] = useState(0);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [error, setError] = useState("");

  const sections = useMemo(() => buildStatusSections(csvData), [csvData]);
  const timerRef = useRef();

  // Fonction pour charger les données CSV
  const loadCsvData = async () => {
    try {
      const response = await fetch(csvUrl + "?t=" + Date.now());
      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const text = await response.text();
      const lines = text.split(/\r?\n/).filter((l) => l.trim());
      if (!lines.length) throw new Error("Fichier CSV vide");

      const [header, ...dataLines] = lines;
      const columns = header.split(",").map((c) => c.trim());

      const rows = dataLines.map((line) => {
        const values = line.split(",").map((v) => v.trim());
        const obj = {};
        columns.forEach((col, idx) => {
          obj[col] = values[idx] || "";
        });
        return mapRow(obj);
      });

      setCsvData(rows);
      setError("");
    } catch (err) {
      console.error("Erreur lors du chargement du CSV:", err);
      setError(`Erreur CSV: ${err.message}`);
    }
  };

  // Effet pour charger les données au montage et configurer le timer
  useEffect(() => {
    loadCsvData();

    if (refreshInterval > 0) {
      timerRef.current = setInterval(loadCsvData, refreshInterval);
      return () => clearInterval(timerRef.current);
    }
  }, [csvUrl, refreshInterval]);

  // Effet pour l'alternance automatique des sections (slideshow)
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSectionIndex((prevIdx) => (prevIdx + 1) % sections.length);
    }, 10000); // Change de section toutes les 10 secondes

    return () => clearInterval(slideTimer);
  }, [sections.length]);

  const currentSection = sections[currentSectionIndex] || { name: "", type: "status", rows: [] };

  return (
    <div className="truck-table-container">
      <header className="table-header">
        <h1>Tableau de Bord - Gestion de Flotte</h1>
        <div className="header-info">
          <span className="last-update">
            Dernière mise à jour: {formatDateTime(lastUpdate)}
          </span>
          <div className="view-indicator">
            Section actuelle: {currentSection.name} ({currentSectionIndex + 1}/{sections.length})
          </div>
        </div>
        {error && <div className="error-message">{error}</div>}
      </header>

      <main className="table-content">
        <StatusView section={currentSection} />
      </main>
    </div>
  );
}
