import React from "react";
import "./TruckTable.css";

const TruckTable = ({ data }) => {
  // Si data est undefined, on met un tableau vide pour éviter les erreurs
  const trucks = data || [];

  return (
    <div className="truck-container">
      <h2 className="truck-title">Tableau des camions</h2>
      <table className="truck-table">
        <thead>
          <tr>
            <th className="col-Unité">Unité</th>
            <th className="col-DATE">DATE</th>
            <th className="col-Date-Réparation">Date Réparation</th>
            <th className="col-Garage">Garage</th>
            <th className="col-Complétée">Complétée</th>
            <th className="col-Prochain-Rendez-vous">Prochain Rendez-vous</th>
            <th className="col-Disponible">Disponible</th>
            <th className="col-Apte-à-rouler">Apte à rouler</th>
            <th className="col-Plainte">Plainte</th>
            <th className="col-Note">Note</th>
          </tr>
        </thead>
        <tbody>
          {trucks.length > 0 ? (
            trucks.map((truck, index) => {
              const rowClass = truck.apteARouler ? "" : "row-non";
              const altClass = index % 2 === 1 && !rowClass ? "row-alt" : "";

              return (
                <tr key={truck.id || index} className={`${rowClass} ${altClass}`}>
                  <td className="col-Unité">{truck.unite || "-"}</td>
                  <td className="col-DATE">{truck.date || "-"}</td>
                  <td className="col-Date-Réparation">{truck.dateReparation || "-"}</td>
                  <td className="col-Garage">{truck.garage || "-"}</td>
                  <td className="col-Complétée">{truck.completee ? "Oui" : "Non"}</td>
                  <td className="col-Prochain-Rendez-vous">{truck.prochainRDV || "-"}</td>
                  <td className={`col-Disponible ${truck.disponible ? "disponible-oui" : "disponible-non"}`}>
                    {truck.disponible ? "Oui" : "Non"}
                  </td>
                  <td className="col-Apte-à-rouler">{truck.apteARouler ? "Oui" : "Non"}</td>
                  <td className="col-Plainte">{truck.plainte || "-"}</td>
                  <td className="col-Note">{truck.note || "-"}</td>
                </tr>
              );
            })
          ) : (
            <tr>
              <td colSpan="10" style={{ textAlign: "center", padding: "20px" }}>
                Chargement des données...
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
