import React, { useEffect, useRef } from "react";
import { getDayClass, isCurrentDay } from "./utils";

export default function ScheduleView({ parsed }) {
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    let scrollPos = 0;
    const scrollStep = 1;
    const delay = 50;
    const pause = 5000;
    let interval;

    const startScroll = () => {
      interval = setInterval(() => {
        if (!containerRef.current) return;
        scrollPos += scrollStep;
        if (scrollPos >= containerRef.current.scrollHeight - containerRef.current.clientHeight) {
          clearInterval(interval);
          setTimeout(() => {
            scrollPos = 0;
            if (containerRef.current) containerRef.current.scrollTop = 0;
            startScroll();
          }, pause);
        } else {
          containerRef.current.scrollTop = scrollPos;
        }
      }, delay);
    };

    startScroll();
    return () => clearInterval(interval);
  }, [parsed]);

  const suppliers = Object.keys(parsed.rows);
  const maxUnits = Math.max(
    ...suppliers.flatMap(s => parsed.rows[s].map(r => r.units.length)),
    1
  );

  return (
    <div className="schedule-container" ref={containerRef} style={{ maxHeight: "100%", overflow: "hidden", display: "flex", flexDirection: "column" }}>
      <div className="schedule-header">
        {parsed.title || "PLANIFICATION ATELIER"}
      </div>
      <table className="schedule-table">
        <thead>
          <tr>
            <th>Fournisseur</th>
            <th>Jour</th>
            {Array.from({ length: maxUnits }, (_, i) => (
              <th key={i}>Unit√© {i+1}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier, supplierIdx) =>
            parsed.rows[supplier].map((r, idx) => {
              const isFirstRow = idx === 0;
              const dayClass = getDayClass(r.jour);
              const isEvenSupplier = supplierIdx % 2 === 0;
              const isTodayRow = isCurrentDay(r.jour);

              return (
                <tr key={`${supplier}-${idx}`} className={`${isFirstRow ? "supplier-first-row" : ""} ${isEvenSupplier ? "supplier-group-even" : ""} ${isTodayRow ? "current-day-row" : ""}`.trim()}>
                  <td>{isFirstRow ? supplier : ""}</td>
                  <td className={dayClass}>{r.jour}</td>
                  {Array.from({ length: maxUnits }, (_, i) => <td key={i}>{r.units[i] || ""}</td>)}
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}
