import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types] = useState(["RO", "CA", "LA", "Autres"]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  const columnWidths = {
    "Unité": 75,
    "DATE": 85,
    "Date Réparation": 40,
    "Garage": 40,
    "Complétée": 40,
    "Prochain Rendez-vous": 60,
    "Disponible": 75,
    "Apte à rouler": 70,
  };

  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const filtered = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Autres";
            const type = ["RO", "CA", "LA"].includes(prefix) ? prefix : "Autres";
            return { ...row, Type: type };
          })
          .filter((r) => r["Unité"]);
        setData(filtered);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const currentType = types[currentTypeIndex];
  const filteredData = data.filter((d) => d.Type === currentType);
  const headers = Object.keys(data[0] || {}).filter((h) => h !== "Type");

  const titleMap = {
    RO: "ROLL-OFF",
    CA: "CHARGEMENT AVANT",
    LA: "CHARGEMENT LATÉRAL",
    Autres: "Autres Camions",
  };

  const narrowCols = [
    "Date Réparation",
    "Garage",
    "Complétée",
    "Prochain Rendez-vous",
  ];

  return (
    <div style={{ width: "100vw", height: "100vh", overflow: "auto", padding: 10 }}>
      {currentType && (
        <h2 style={{ textAlign: "center", marginBottom: 20, fontWeight: "bold", color: "#333" }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table
        style={{
          borderCollapse: "collapse",
          width: "100%",        
          tableLayout: "fixed",
          margin: "0 auto",
        }}
      >
        <colgroup>
          {headers.map((h) => {
            if (h === "Plainte" || h === "Note") {
              return <col key={h} />; // Sans largeur fixe
            }
            const w = columnWidths[h] || 40;
            return (
              <col
                key={h}
                style={{ width: w, minWidth: w, maxWidth: w }}
              />
            );
          })}
        </colgroup>
        <thead>
          <tr>
            {headers.map((h) => {
              const isDateRep = h.toLowerCase() === "date réparation" || h.toLowerCase() === "datereparation";
              const isProchainRDV = h.toLowerCase() === "prochain rendez-vous" || h.toLowerCase() === "prochainrendezvous";
              const fixedWidth = columnWidths[h] || (h === "Plainte" || h === "Note" ? undefined : 40);
              return (
                <th
                  key={h}
                  style={{
                    border: "2px solid black",
                    backgroundColor: "#181818",
                    color: "white",
                    padding: isDateRep || isProchainRDV ? "4px 8px" : "6px 8px",
                    textTransform: "uppercase",
                    whiteSpace: "nowrap",
                    lineHeight: isDateRep || isProchainRDV ? "1.1em" : undefined,
                    verticalAlign: "bottom",
                    textAlign: "center",
                    width: fixedWidth,
                    minWidth: fixedWidth,
                    maxWidth: fixedWidth,
                  }}
                >
                  {isDateRep ? (
                    <>
                      Date
                      <br />
                      Réparation
                    </>
                  ) : isProchainRDV ? (
                    <>
                      PROCHAIN
                      <br />
                      RENDEZ-VOUS
                    </>
                  ) : (
                    h
                  )}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers.find((h) => h.toLowerCase().includes("apte à rouler"));
            const isNon = row[aptColKey]?.toLowerCase() === "non";
            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? "#d32f2f" : idx % 2 === 1 ? "#edeff2" : "white",
                  color: isNon ? "white" : "black",
                }}
              >
                {headers.map((col) => {
                  const fixedWidth = columnWidths[col] || (col === "Plainte" || col === "Note" ? undefined : 40);
                  return (
                    <td
                      key={col}
                      style={{
                        border: "2px solid black",
                        whiteSpace: narrowCols.includes(col) ? "nowrap" : col === "Plainte" || col === "Note" ? "normal" : "normal",
                        overflowWrap: col === "Plainte" || col === "Note" ? "break-word" : undefined,
                        wordBreak: col === "Plainte" || col === "Note" ? "break-word" : undefined,
                        overflow: narrowCols.includes(col) ? "hidden" : undefined,
                        textOverflow: narrowCols.includes(col) ? "ellipsis" : undefined,
                        textAlign: col === "Plainte" || col === "Note" ? "left" : "center",
                        padding: 8,
                        verticalAlign: "top",
                        width: fixedWidth,
                        minWidth: fixedWidth,
                        maxWidth: fixedWidth,
                      }}
                    >
                      {row[col]}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
