import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types, setTypes] = useState([]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);
  const [columnWidths, setColumnWidths] = useState({});

  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const rows = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Inconnu";
            let type;
            if (prefix === "RO" || prefix === "CA" || prefix === "LA") {
              type = prefix;
            } else {
              type = "Autres";
            }
            row.Type = type;
            return row;
          })
          .filter((r) => r.Type !== "Inconnu");
        setData(rows);
        setTypes(["RO", "CA", "LA", "Autres"]);
        setCurrentTypeIndex(0);

        const headers = results.meta.fields.filter((f) => f !== "Type");
        let widths = {};
        headers.forEach((h) => {
          if (h.toUpperCase().replace(/\s/g, "") === "DATEREPARATION") widths[h] = 50;
          else if (h.toUpperCase().replace(/\s|-/g, "") === "PROCHAINERENDEZVOUS") widths[h] = 50;
          else if (h === "Plainte") widths[h] = undefined; // dynamique
          else widths[h] = 100;
        });
        setColumnWidths(widths);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const headers = data.length > 0 ? Object.keys(data[0]).filter((k) => k !== "Type") : [];
  const titleMap = {
    RO: "ROLL-OFF",
    CA: "CHARGEMENT AVANT",
    LA: "CHARGEMENT LATÉRAL",
    Autres: "Autres Camions",
  };
  const currentType = types[currentTypeIndex] || null;
  const filteredData = data.filter((d) => d.Type === currentType);

  return (
    <div style={{ width: "100vw", height: "100vh", overflow: "auto", padding: 10 }}>
      {currentType && (
        <h2 style={{ textAlign: "center", marginBottom: 20, fontWeight: "bold", color: "#333" }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: "collapse", width: "100%", tableLayout: "fixed" }}>
        <colgroup>
          {headers.map((h) => {
            if (columnWidths[h]) {
              const w = columnWidths[h];
              return <col key={h} style={{ width: w, minWidth: w, maxWidth: w }} />;
            }
            return <col key={h} />;
          })}
        </colgroup>
        <thead>
          <tr>
            {headers.map((h) => {
              const isDateRep = h.toUpperCase().replace(/\s/g, "") === "DATEREPARATION";
              const isProchaineRDV = h.toUpperCase().replace(/\s|-/g, "") === "PROCHAINERENDEZVOUS";
              return (
                <th
                  key={h}
                  style={{
                    border: "2px solid black",
                    backgroundColor: "#181818",
                    color: "white",
                    padding: isDateRep || isProchaineRDV ? "6px 10px" : "6px 8px",
                    textTransform: "uppercase",
                    whiteSpace: "normal",
                    verticalAlign: "bottom",
                    textAlign: "center",
                  }}
                >
                  {isDateRep ? (
                    <>
                      Date
                      <br />
                      Réparation
                    </>
                  ) : isProchaineRDV ? (
                    <>
                      PROCHAIN
                      <br />
                      RENDEZ-VOUS
                    </>
                  ) : (
                    h
                  )}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey =
              headers.find((h) => h.toLowerCase() === "apte à rouler" || h.toLowerCase() === "apte a rouler") || headers[7];
            const aptVal = row[aptColKey] ? row[aptColKey].toLowerCase() : "";
            const isNon = aptVal === "non";
            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? "#d32f2f" : idx % 2 === 1 ? "#edeff2" : "white",
                  color: isNon ? "white" : "black",
                }}
              >
                {headers.map((col, ci) => (
                  <td
                    key={ci}
                    style={{
                      border: "2px solid black",
                      whiteSpace: "normal",
                      textAlign: col === "Plainte" ? "left" : "center",
                      padding: 8,
                      verticalAlign: col.toUpperCase() === "DISPONIBLE" ? "middle" : "top",
                    }}
                  >
                    {row[col]}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
