import React, { useEffect, useMemo, useRef, useState } from "react";
import "./TruckTable.css";

/* ========================
   Utilitaires communs
======================== */

function toBoolOuiNon(val) {
  const v = (val ?? "").toString().trim().toLowerCase();
  if (v === "oui" || v === "yes" || v === "true") return true;
  if (v === "non" || v === "no" || v === "false") return false;
  return null;
}

function mapRow(obj) {
  return {
    unite: obj?.Unit?.trim() ?? obj?.UNIT?.trim() ?? obj?.UNITE?.trim() ?? obj?.UNITÉ?.trim(),
    date: obj?.DATE?.trim(),
    plainte: obj?.PLAINTE?.trim(),
    dateReparation: obj?.["DATE RÉPARATION"]?.trim() ?? obj?.["DATE REPARATION"]?.trim(),
    garage: obj?.GARAGE?.trim(),
    completee: obj?.COMPLÉTÉE?.trim() ?? obj?.["COMPLÉTÉ"]?.trim() ?? obj?.COMPLÊTE?.trim(),
    prochainRdv:
      obj?.["PROCHAINE DATE DE RÉPARATION"]?.trim() ??
      obj?.["PROCHAIN RENDEZ-VOUS"]?.trim() ??
      obj?.["PROCHAINE DATE DE REPARATION"]?.trim(),
    disponible:
      toBoolOuiNon(obj?.DISPONIBLE ?? obj?.Disponible ?? obj?.["APTE À ROULER"] ?? obj?.["APTE A ROULER"]),
    note: obj?.NOTE?.trim(),
  };
}

function categorize(unite) {
  const u = (unite ?? "").trim().toUpperCase();
  if (u.startsWith("RO")) return "ROLL-OFF";
  if (u.startsWith("CA")) return "CHARGEMENT AVANT";
  if (u.startsWith("LA")) return "CHARGEMENT LATÉRAL";
  return "Autres camions";
}

function buildStatusSections(rows) {
  const order = ["ROLL-OFF", "CHARGEMENT AVANT", "CHARGEMENT LATÉRAL", "Autres camions"];
  const buckets = Object.fromEntries(order.map(k => [k, []]));
  for (const r of rows) {
    const cat = categorize(r.unite);
    if (!buckets[cat]) buckets[cat] = [];
    buckets[cat].push(r);
  }
  return { order, buckets };
}

/* ========================
   Planning atelier (CSV)
======================== */

function isUnitLabel(labelRaw) {
  const label = (labelRaw ?? "")
    .toString()
    .normalize("NFD")
    .replace(/\p{Diacritic}/gu, "")
    .trim()
    .toUpperCase(); // "UNITÉ" -> "UNITE"
  return label === "UNITE" || label === "UNIT" || label === "UNITES" || label === "UNITÉ";
}

/**
 * parseSheduleText
 * Attend un CSV sans en-têtes, semicolons ;, où chaque ligne utile ressemble à :
 * ;Globocam;LUNDI;UNITE;ro1000;UNITE;ro1001;UNITE;ro1002
 * La toute première cellule de la 1ère ligne est utilisée comme titre (ex: "SEMAINE DU 22 SEPTEMBRE 2025").
 */
function parseSheduleText(text) {
  const clean = (text ?? "").replace(/\r/g, "");
  const lines = clean.split("\n").filter(l => l.trim().length > 0);
  if (lines.length === 0) return { title: "", rows: {} };

  // Titre dans la première cellule de la première ligne
  const firstCells = lines[0].split(";").map(c => c.trim());
  const title = firstCells[0] || "";

  const dataLines = lines.slice(1);
  const flatRows = [];
  let currentSupplier = "";

  for (const raw of dataLines) {
    const cells = raw.split(";").map(c => c.trim());
    if (cells.every(c => !c)) continue;

    // Par convention: index 1 = Fournisseur, index 2 = Jour, puis paires UNITE;valeur...
    const supplier = cells[1] || currentSupplier || "";
    const jour = cells[2] || "";
    if (supplier) currentSupplier = supplier;

    const unitValues = [];
    for (let i = 3; i < cells.length; i++) {
      const label = cells[i] ?? "";
      const val = cells[i + 1] ?? "";
      if (isUnitLabel(label) && val) {
        unitValues.push(val);
        i += 1;
      }
    }

    // Ignorer les lignes sans jour et sans unités
    if (!jour && unitValues.length === 0) continue;

    flatRows.push({
      supplier,
      jour,
      units: unitValues,
    });
  }

  // Regrouper par fournisseur
  const grouped = {};
  for (const r of flatRows) {
    const key = r.supplier || "(Inconnu)";
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(r);
  }

  return { title, rows: grouped };
}

function ScheduleView({ scheduleText }) {
  const parsed = useMemo(() => parseSheduleText(scheduleText), [scheduleText]);
  const suppliers = Object.keys(parsed.rows);

  // Nombre max de colonnes d'unités sur une ligne
  let computedMaxUnits = 0;
  for (const s of suppliers) {
    for (const r of parsed.rows[s]) {
      if ((r.units?.length ?? 0) > computedMaxUnits) computedMaxUnits = r.units.length;
    }
  }

  // Si on veut toujours au moins 19 colonnes visibles, décommenter:
  // const maxUnits = Math.max(19, computedMaxUnits);
  const maxUnits = computedMaxUnits;

  const unitHeaders = Array.from({ length: maxUnits }, (_, i) => `UNITE${i + 1}`);

  return (
    <div className="schedule-container">
      <div className="schedule-header">{parsed.title || "PLANIFICATION ATELIER"}</div>
      <table className="schedule-table">
        <thead>
          <tr>
            <th style={{ width: 160 }}>Fournisseur</th>
            <th style={{ width: 140 }}>Jour</th>
            <th className="schedule-col-label" style={{ width: 60, textAlign: "center" }}>
              UNITÉ
            </th>
            {unitHeaders.map((h, i) => (
              <th key={i} className="schedule-col-unit">
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {suppliers.map(supplier =>
            parsed.rows[supplier].map((r, idx) => (
              <tr key={`${supplier}-${idx}`}>
                <td>{idx === 0 ? supplier : ""}</td>
                <td>{r.jour}</td>
                <td className="schedule-col-label">UNITÉ</td>
                {Array.from({ length: maxUnits }).map((_, i) => (
                  <td key={i} className="schedule-col-unit">
                    {(r.units?.[i] ?? "").toString()}
                  </td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

/* ========================
   Données & affichage
======================== */

export default function TruckTable() {
  const [statusRows, setStatusRows] = useState([]);
  const [scheduleText, setScheduleText] = useState("");
  const [autoScroll, setAutoScroll] = useState(true);
  const [intervalSec, setIntervalSec] = useState(15);
  const scrollerRef = useRef(null);

  // EXEMPLE: lecture des fichiers statiques servis par l’app (public/…)
  useEffect(() => {
    // 1) Charger CSV planning
    fetch("shedule.csv")
      .then(r => r.text())
      .then(setScheduleText)
      .catch(() => setScheduleText(""));

    // 2) Charger un CSV/JSON des statuts camions (si vous en avez un)
    // Remplacez par votre source réelle.
    fetch("truck_status.json")
      .then(r => r.json())
      .then(arr => {
        const mapped = Array.isArray(arr) ? arr.map(mapRow) : [];
        setStatusRows(mapped);
      })
      .catch(() => setStatusRows([]));
  }, []);

  // Auto-défilement vertical optionnel
  useEffect(() => {
    if (!autoScroll) return;
    const node = scrollerRef.current;
    if (!node) return;
    let dir = 1;
    const id = setInterval(() => {
      if (!node) return;
      if (node.scrollTop + node.clientHeight >= node.scrollHeight) dir = -1;
      if (node.scrollTop <= 0) dir = 1;
      node.scrollTop += dir * 2;
    }, 50);
    return () => clearInterval(id);
  }, [autoScroll]);

  const { order, buckets } = useMemo(() => buildStatusSections(statusRows), [statusRows]);

  return (
    <div className="truck-container">
      <div className="slider-bar">
        <label>
          Défilement auto
          <input
            type="checkbox"
            checked={autoScroll}
            onChange={e => setAutoScroll(e.target.checked)}
            style={{ marginLeft: 8 }}
          />
        </label>
        <span className="slider-info">Interval: {intervalSec}s</span>
      </div>

      {/* Planning atelier */}
      <ScheduleView scheduleText={scheduleText} />

      {/* Tableau statuts par catégories */}
      <div
        ref={scrollerRef}
        style={{
          maxHeight: "45vh",
          overflow: "auto",
          marginTop: 12,
          border: "1px solid #999",
          padding: 8,
          background: "#fff",
        }}
      >
        {order.map(section => (
          <div key={section} style={{ marginBottom: 16 }}>
            <h3 className="truck-title">{section}</h3>
            <table className="truck-table">
              <thead>
                <tr>
                  <th className="col-Unit">UNITÉ</th>
                  <th className="col-DATE">DATE</th>
                  <th className="col-Plainte">PLAINTE</th>
                  <th className="col-Date-Rparation">DATE RÉPARATION</th>
                  <th className="col-Garage">GARAGE</th>
                  <th className="col-Complte">COMPLÉTÉ</th>
                  <th className="col-Prochain-Rendez-vous">PROCHAIN RENDEZ-VOUS</th>
                  <th className="col-Disponible">DISPONIBLE</th>
                  <th className="col-Note">NOTE</th>
                </tr>
              </thead>
              <tbody>
                {(buckets[section] ?? []).map((r, i) => (
                  <tr key={i}>
                    <td>{r.unite}</td>
                    <td>{r.date}</td>
                    <td className="wrap">{r.plainte}</td>
                    <td className="wrap">{r.dateReparation}</td>
                    <td>{r.garage}</td>
                    <td>{r.completee}</td>
                    <td className="wrap">{r.prochainRdv}</td>
                    <td style={{ textAlign: "center" }}>
                      {r.disponible === null ? "" : r.disponible ? "Oui" : "Non"}
                    </td>
                    <td className="wrap">{r.note}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
}
