import React, { useEffect, useState, useRef } from "react";
import ScheduleView from "./ScheduleView";
import "./TruckTable.css";

// Helper pour format date
const formatDateTime = (d) => {
  try {
    return new Intl.DateTimeFormat("fr-CA", { dateStyle: "medium", timeStyle: "short" }).format(d);
  } catch {
    return d?.toString() || "";
  }
};

export default function TruckTable() {
  const [rows, setRows] = useState([]);
  const [scheduleParsed, setScheduleParsed] = useState({ title: "", rows: {} });
  const [index, setIndex] = useState(0);
  const timerRef = useRef(null);
  const pausedRef = useRef(false);

  // Charge truck_status.csv
  useEffect(() => {
    async function loadStatus() {
      try {
        const res = await fetch("/truck_status.csv", { cache: "no-store" });
        if (!res.ok) return;
        const text = await res.text();
        const lines = text.replace(/^\uFEFF/, "").split(/\r?\n/).filter(l => l.trim());
        if (!lines.length) return;
        const headers = lines[0].split(";").map(h => h.trim());
        const data = lines.slice(1).map(line => {
          const cells = line.split(";");
          const obj = {};
          headers.forEach((h, i) => obj[h] = cells[i] || "");
          return obj;
        });
        setRows(data);
      } catch (e) {
        console.error(e);
      }
    }
    loadStatus();
  }, []);

  // Charge schedule.csv
  useEffect(() => {
    async function loadSchedule() {
      try {
        const res = await fetch("/schedule.csv", { cache: "no-store" });
        if (!res.ok) return;
        const text = await res.text();
        const clean = text.replace(/^\uFEFF/, "");
        const lines = clean.split(/\r?\n/).filter(l => l.trim());
        if (!lines.length) return;
        const title = lines[0].split(";").filter(c => c).join(" ");
        const grouped = {};
        let currentSupplier = "";
        for (let i = 1; i < lines.length; i++) {
          const cells = lines[i].split(";").map(c => c.trim());
          const supplier = cells[0] || currentSupplier;
          currentSupplier = supplier || currentSupplier;
          const jour = cells[1] || "";
          const units = cells.slice(2).filter(Boolean);
          if (!grouped[currentSupplier]) grouped[currentSupplier] = [];
          grouped[currentSupplier].push({ supplier: currentSupplier, jour, units });
        }
        setScheduleParsed({ title, rows: grouped });
      } catch (e) {
        console.error(e);
      }
    }
    loadSchedule();
  }, []);

  const sections = [
    { name: "Camions", type: "status", rows },
    { name: "Planification atelier", type: "schedule", rows: [] }
  ];

  const current = sections[index] || sections[0];

  // Auto-slide 15s
  useEffect(() => {
    clearInterval(timerRef.current);
    if (!pausedRef.current && sections.length > 0) {
      timerRef.current = setInterval(() => {
        setIndex(i => (i + 1) % sections.length);
      }, 15000);
    }
    return () => clearInterval(timerRef.current);
  }, [sections.length]);

  const onMouseEnter = () => {
    pausedRef.current = true;
    clearInterval(timerRef.current);
  };
  const onMouseLeave = () => {
    pausedRef.current = false;
    clearInterval(timerRef.current);
    timerRef.current = setInterval(() => setIndex(i => (i + 1) % sections.length), 15000);
  };

  const prev = () => setIndex(i => i === 0 ? sections.length - 1 : i - 1);
  const next = () => setIndex(i => (i + 1) % sections.length);

  return (
    <div className="truck-container" onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
      <div className="slider-bar">
        <button onClick={prev}>Précédent</button>
        <h2 className="truck-title">{current.name}</h2>
        <button onClick={next}>Suivant</button>
        <div className="slider-info">Défilement auto: 15s</div>
      </div>

      {current.type === "schedule" ? (
        <ScheduleView parsed={scheduleParsed} />
      ) : (
        <table className="truck-table">
          <thead>
            <tr>
              <th className="col-Unité">Unité</th>
              <th className="col-DATE">DATE</th>
              <th className="col-Date-Réparation">DATE RÉPARATION</th>
              <th className="col-Garage">GARAGE</th>
              <th className="col-Complétée">COMPLÉTÉE</th>
              <th className="col-Prochain-Rendez-vous">PROCHAIN RENDEZ-VOUS</th>
              <th className="col-Disponible">DISPONIBLE</th>
              <th className="col-Plainte">PLAINTE</th>
              <th className="col-Note">NOTE</th>
            </tr>
          </thead>
          <tbody>
            {rows.length > 0 ? rows.map((row, idx) => (
              <tr key={idx} className={`${idx % 2 ? "row-alt" : ""} ${row.disponible === "oui" ? "disponible-oui" : ""} ${row.disponible === "non" ? "disponible-non" : ""} ${row.status === "unavailable" ? "row-unavailable" : ""}`}>
                <td className="col-Unité">{row.unite}</td>
                <td className="col-DATE">{row.date}</td>
                <td className="col-Date-Réparation">{row.dateReparation}</td>
                <td className="col-Garage">{row.garage}</td>
                <td className="col-Complétée">{row.completee}</td>
                <td className="col-Prochain-Rendez-vous">{row.prochainRdv}</td>
                <td className="col-Disponible">{row.disponible}</td>
                <td className="col-Plainte">{row.plainte}</td>
                <td className="col-Note">{row.note}</td>
              </tr>
            )) : <tr><td colSpan={9}>Aucune donnée disponible</td></tr>}
          </tbody>
        </table>
      )}

      <div className="footer-bar">
        <div className="last-updated">
          Dernière mise à jour: {formatDateTime(new Date())}
        </div>
      </div>
    </div>
  );
}
