import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';

const TruckTable = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    Papa.parse('/truck_status.csv', {
      download: true,
      header: true,
      complete: results => {
        const rows = results.data.map(row => {
          const type = row['Unité']?.substring(0,2).toUpperCase();
          row.Type = (type === 'TR' || type === 'PU' || type === 'PM') ? 'TR/PU/PM' : type || 'Inconnu';
          return row;
        });
        setData(rows.filter(r => r.Type !== 'Inconnu'));
      },
    });
  }, []);

  const headers = data.length > 0 ? Object.keys(data[0]).filter(k => k !== 'Type') : [];

  return (
    <div style={{width:'100vw', height:'100vh', overflow:'auto'}}>
      <table style={{borderCollapse: 'collapse', width: '100%', tableLayout: 'fixed'}}>
        <thead>
          <tr>
            {headers.map((h,i) => (
              <th key={h} style={{
                border: '2px solid black',
                backgroundColor: '#181818',
                color: 'white',
                padding: '10px',
                textTransform: 'uppercase',
                whiteSpace: 'nowrap',
                width: h === 'Unité' ? 115 : h === 'DATE' ? 90 : 'auto',
                minWidth: h === 'Unité' ? 115 : h === 'DATE' ? 90 : 100,
                textAlign: h === 'Plainte' ? 'left' : 'center',
                }}>
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, idx) => {
            const status = row[headers[7]]?.toLowerCase();
            const isNon = status === 'non';
            return (
              <tr key={idx} style={{backgroundColor: isNon ? '#d32f2f' : idx % 2 === 1 ? '#edeff2' : 'white', color: isNon ? 'white' : 'black'}}>
                {headers.map((col, ci) => {
                  const val = row[col] || '';
                  let cellStyle = {border: '2px solid black', padding: '8px', overflowWrap:'break-word'};
                  if (col === 'Unité') {
                    cellStyle.backgroundColor = '#1565c0';
                    cellStyle.color = 'white';
                    cellStyle.fontWeight = 'bold';
                    cellStyle.width = 115;
                    cellStyle.minWidth = 115;
                  } else if (col === 'DATE') {
                    cellStyle.width = 90;
                    cellStyle.minWidth = 90;
                  } else if (col === 'Plainte') {
                    cellStyle.textAlign = 'left';
                  }
                  if (col === headers[7] && val.toLowerCase() === 'oui' && !isNon) {
                    cellStyle.backgroundColor = '#43a047';
                    cellStyle.color = 'white';
                    cellStyle.fontWeight = 'bold';
                    cellStyle.borderRadius = '3px';
                    cellStyle.letterSpacing = '1px';
                  }
                  // Affiche tiret si Date vide
                  const displayVal = (col === 'DATE' && (val.trim() === '' || !val)) ? '-' : val;
                  return <td key={ci} style={cellStyle}>{displayVal}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
