import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types] = useState(["RO", "CA", "LA", "Autres"]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  const columnWidths = {
    "Unité": 90,
    "DATE": 80,
    "Date Réparation": 800,
    "Garage": 50,
    "Complétée": 50,
    "Prochain Rendez-vous": 110,
    "Disponible": 90,
    "Apte à rouler": 70,
  };

  const fixedColumns = Object.keys(columnWidths);

  const titleMap = {
    "Date Réparation": (
      <>
        DATE
        <br />
        RÉPARATION
      </>
    ),
    "Prochain Rendez-vous": (
      <>
        PROCHAIN
        <br />
        RENDEZ-VOUS
      </>
    ),
  };

  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const filtered = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Autres";
            const type = ["RO", "CA", "LA"].includes(prefix) ? prefix : "Autres";
            return { ...row, Type: type };
          })
          .filter((r) => r["Unité"]);
        setData(filtered);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const currentType = types[currentTypeIndex];
  const filteredData = data.filter((d) => d.Type === currentType);
  const headers = Object.keys(data[0] || {}).filter((h) => h !== "Type");

  const narrowCols = [
    "Date Réparation",
    "Garage",
    "Complétée",
    "Prochain Rendez-vous",
  ];

  return (
    <div style={{ width: "1880px", height: "100vh", overflow: "auto", padding: 10 }}>
      <table
        style={{
          borderCollapse: "collapse",
          width: "100%",
          tableLayout: "fixed", // CRUCIAL pour les largeurs !
          margin: "0 auto",
        }}
      >
        <colgroup>
          {headers.map((h) => {
            if (fixedColumns.includes(h)) {
              const w = columnWidths[h];
              return <col key={h} style={{ width: w, minWidth: w, maxWidth: w }} />;
            }
            return <col key={h} />;
          })}
        </colgroup>
        <thead>
          <tr>
            {headers.map((h) => {
              const isFixed = fixedColumns.includes(h);
              const isTwoLineTitle = h === "Date Réparation" || h === "Prochain Rendez-vous";
              const width = isFixed ? columnWidths[h] : undefined;
              const isUnite = h === "Unité";
              return (
                <th
                  key={h}
                  style={{
                    width,
                    minWidth: width,
                    maxWidth: width,
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                    whiteSpace: isTwoLineTitle ? "normal" : "nowrap",
                    textAlign: "center",
                    border: "2px solid black",
                    backgroundColor: isUnite ? "#0057b7" : "#181818",
                    color: "white",
                    padding: "6px 8px",
                    fontWeight: isUnite ? "bold" : "normal",
                    lineHeight: isTwoLineTitle ? "1.1em" : undefined,
                  }}
                >
                  {titleMap[h] || h}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers.find((h) => h.toLowerCase().includes("apte à rouler"));
            const isNon = row[aptColKey]?.toLowerCase() === "non";
            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? "#d32f2f" : idx % 2 === 1 ? "#edeff2" : "white",
                  color: isNon ? "white" : "black",
                }}
              >
                {headers.map((col) => {
                  const isFixed = fixedColumns.includes(col);
                  const width = isFixed ? columnWidths[col] : undefined;
                  const isPlainteOrNote = col === "Plainte" || col === "Note";
                  const isDisponible = col === "Disponible";
                  return (
                    <td
                      key={col}
                      style={{
                        width,
                        minWidth: width,
                        maxWidth: width,
                        whiteSpace: narrowCols.includes(col)
                          ? "nowrap"
                          : isPlainteOrNote
                          ? "normal"
                          : "normal",
                        overflowWrap: isPlainteOrNote ? "break-word" : undefined,
                        wordBreak: isPlainteOrNote ? "break-word" : undefined,
                        overflow: narrowCols.includes(col) ? "hidden" : undefined,
                        textOverflow: narrowCols.includes(col) ? "ellipsis" : undefined,
                        border: "2px solid black",
                        textAlign: isPlainteOrNote ? "left" : "center",
                        padding: 8,
                        verticalAlign: "top",
                        backgroundColor: idx % 2 === 1 && col !== "Disponible" ? "#edeff2" : "white",
                        color: isDisponible ? (row[col]?.toLowerCase() === "oui" ? "green" : row[col]?.toLowerCase() === "non" ? "red" : "black") : undefined,
                        fontWeight: isDisponible ? "bold" : undefined,
                      }}
                    >
                      {row[col]}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
