import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types, setTypes] = useState([]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);
  const [columnWidths, setColumnWidths] = useState({});

  useEffect(() => {
    Papa.parse('/truck_status.csv', {
      download: true,
      header: true,
      complete: results => {
        const rows = results.data.map(row => {
          const prefix = row['Unité']?.substring(0, 2).toUpperCase() || 'Inconnu';
          let type;
          if (prefix === 'RO' || prefix === 'CA' || prefix === 'LA') {
            type = prefix;
          } else {
            type = 'Autres';
          }
          row.Type = type;
          return row;
        }).filter(r => r.Type !== 'Inconnu');

        setData(rows);

        const uniqueTypes = ['RO', 'CA', 'LA', 'Autres'];
        setTypes(uniqueTypes);
        setCurrentTypeIndex(0);

        // Calcul largeur max par colonne
        const headers = results.meta.fields.filter(f => f !== 'Type');
        let widths = {};
        headers.forEach(h => {
          let maxLen = h.length;
          rows.forEach(row => {
            const val = row[h] ? String(row[h]) : '';
            if (val.length > maxLen) maxLen = val.length;
          });
          if (h === 'Plainte') widths[h] = Math.max(200, Math.min(maxLen * 7, 900));
          else if (h === 'Unité') widths[h] = 115;
          else if (h.toUpperCase() === 'DATE') widths[h] = 70;
          else if (h.toUpperCase().replace(/\s/g,'') === 'DATEREPARATION') widths[h] = 85;
          else if (h.toUpperCase().includes('COMPLÉTÉ') || h.toUpperCase().includes('COMPLETEE')) widths[h] = 85;
          else if (h.toUpperCase().includes('PROCHAINERENDEZVOUS')) widths[h] = 85;
          else if (h.toUpperCase() === 'DISPONIBLE') widths[h] = 85;
          else if (h === 'GARAGE') widths[h] = 120;
          else widths[h] = Math.max(85, Math.min(maxLen * 7, 220));
        });
        setColumnWidths(widths);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex(prev => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const headers = data.length > 0 ? Object.keys(data[0]).filter(k => k !== 'Type') : [];

  const titleMap = {
    RO: 'Rolloff',
    CA: 'Chargement Avant',
    LA: 'Chargement Latéral',
    Autres: 'Autres Camions',
  };

  const currentType = types[currentTypeIndex] || null;

  const filteredData = data.filter(d => d.Type === currentType);

  return (
    <div style={{ width: '100vw', height: '100vh', overflow: 'auto', padding: '10px' }}>
      {currentType && (
        <h2 style={{ textAlign: 'center', marginBottom: '20px', fontWeight: 'bold', color: '#333' }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: 'collapse', width: '100%', tableLayout: 'fixed' }}>
        <thead>
          <tr>
            {headers.map(h => {
              const isDateRep = h.toUpperCase().replace(/\s/g,'') === 'DATEREPARATION';
              const isProchaineRDV = h.toUpperCase().replace(/\s|-/g,'') === 'PROCHAINERENDEZVOUS';
              const isCompletee = h.toUpperCase().includes('COMPLÉT') || h.toUpperCase().includes('COMPLETEE');
              const isDisponible = h.toUpperCase() === 'DISPONIBLE';
              const width = columnWidths[h] || 100;

              return (
                <th
                  key={h}
                  style={{
                    border: '2px solid black',
                    backgroundColor: '#181818',
                    color: 'white',
                    padding: '4px 8px',
                    textTransform: 'uppercase',
                    whiteSpace: 'normal',
                    textAlign: 'center',
                    width,
                    minWidth: width,
                    maxWidth: width,
                    wordBreak: isDateRep || isProchaineRDV ? 'break-word' : undefined,
                    lineHeight: isDateRep || isProchaineRDV ? '1.1em' : undefined,
                    verticalAlign: 'bottom',
                  }}
                  title={h}
                >
                  {isDateRep ? (
                    <>Date<br />Réparation</>
                  ) : isProchaineRDV ? (
                    <>Prochain<br />Rendez-vous</>
                  ) : (
                    h
                  )}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers[7];
            const aptVal = row[aptColKey] ? row[aptColKey].toLowerCase() : '';
            const isNon = aptVal === 'non';

            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? '#d32f2f' : idx % 2 === 1 ? '#edeff2' : 'white',
                  color: isNon ? 'white' : 'black',
                }}
              >
                {headers.map((col, ci) => {
                  const isDateRep = col.toUpperCase().replace(/\s/g,'') === 'DATEREPARATION';
                  const isProchaineRDV = col.toUpperCase().replace(/\s|-/g,'') === 'PROCHAINERENDEZVOUS';
                  const isCompletee = col.toUpperCase().includes('COMPLÉT') || col.toUpperCase().includes('COMPLETEE');
                  const isDisponible = col.toUpperCase() === 'DISPONIBLE';

                  const width = columnWidths[col] || 100;

                  let tdStyle = {
                    border: '2px solid black',
                    whiteSpace: 'normal',
                    textAlign: 'center',
                    width,
                    minWidth: width,
                    maxWidth: width,
                    padding: '8px',
                    verticalAlign: isDisponible ? 'middle' : 'top',
                    overflowWrap: 'break-word',
                  };

                  if (col === 'Unité') {
                    tdStyle.backgroundColor = '#1565c0';
                    tdStyle.color = 'white';
                    tdStyle.fontWeight = 'bold';
                  } else if (col === 'DATE') {
                    tdStyle.textAlign = 'center';
                  } else if (col === 'Plainte') {
                    tdStyle.textAlign = 'left';
                  } else if (col === 'GARAGE') {
                    tdStyle.textAlign = 'center';
                  } else if (col === 'Apte à rouler') {
                    tdStyle.textAlign = 'center';
                    if (row[col]?.toLowerCase() === 'oui' && !isNon) {
                      tdStyle.backgroundColor = '#43a047';
                      tdStyle.color = 'white';
                      tdStyle.fontWeight = 'bold';
                      tdStyle.borderRadius = '3px';
                      tdStyle.letterSpacing = '1px';
                    }
                  } else if (isCompletee) {
                    tdStyle.textAlign = 'center';
                  } else if (isDisponible) {
                    tdStyle.textAlign = 'center';
                    tdStyle.verticalAlign = 'middle';
                  }

                  if (col === aptColKey && row[col]?.toLowerCase() === 'oui' && !isNon) {
                    row[col] = row[col].trim();
                  }

                  if ((col === 'DATE' || isDateRep) && (!row[col] || row[col].trim() === '')) row[col] = '-';

                  return <td key={ci} style={tdStyle}>{row[col]}</td>;
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
