import React, { useEffect, useState } from 'react';
import Papa from 'papaparse';

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types, setTypes] = useState([]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  useEffect(() => {
    Papa.parse('/truck_status.csv', {
      download: true,
      header: true,
      complete: results => {
        // Regroupement par 2 premières lettres Unité
        const rows = results.data.map(row => {
          const prefix = row['Unité']?.substring(0, 2).toUpperCase() || 'Inconnu';
          let type;
          if (prefix === 'RO' || prefix === 'CA' || prefix === 'LA') {
            type = prefix;
          } else {
            type = 'Autres';
          }
          row.Type = type;
          return row;
        }).filter(r => r.Type !== 'Inconnu');

        setData(rows);

        // Extraire les types uniques pour slideshow
        const uniqueTypes = ['RO', 'CA', 'LA', 'Autres'];
        setTypes(uniqueTypes);
        setCurrentTypeIndex(0);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex(prev => (prev + 1) % types.length);
    }, 10000);

    return () => clearInterval(interval);
  }, [types]);

  const headers = data.length > 0 ? Object.keys(data[0]).filter(k => k !== 'Type') : [];

  const currentType = types[currentTypeIndex] || null;

  const filteredData = data.filter(d => d.Type === currentType);

  return (
    <div style={{width:'100vw', height:'100vh', overflow:'auto', padding: '10px'}}>
      {currentType && (
        <h2 style={{ textAlign: 'center', marginBottom: '20px', fontWeight: 'bold', color: '#333' }}>
          {currentType === 'Autres' ? 'Autres Types' : `Type : ${currentType}`}
        </h2>
      )}
      <table style={{borderCollapse: 'collapse', width: '100%', tableLayout: 'fixed'}}>
        <thead>
          <tr>
            {headers.map(h => (
              <th key={h} style={{
                border: '2px solid black',
                backgroundColor: '#181818',
                color: 'white',
                padding: '10px',
                textTransform: 'uppercase',
                whiteSpace: 'nowrap',
                textAlign: 'center',
                width:
                  h === 'Unité' ? 115
                  : h === 'DATE' ? 70
                  : h === 'Plainte' ? 'auto'
                  : h === 'GARAGE' ? 120
                  : h === 'Apte à rouler' ? 60
                  : 'auto',
                minWidth:
                  h === 'Unité' ? 115
                  : h === 'DATE' ? 70
                  : h === 'Plainte' ? 220
                  : h === 'GARAGE' ? 80
                  : h === 'Apte à rouler' ? 60
                  : 100,
                maxWidth:
                  h === 'Plainte' ? 1000
                  : h === 'GARAGE' ? 150
                  : h === 'Apte à rouler' ? 60
                  : undefined
              }}>
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            // Colonne H (index 7) = Apte à rouler dynamique couleur
            const aptColKey = headers[7];
            const aptVal = row[aptColKey] ? row[aptColKey].toLowerCase() : '';
            const isNon = aptVal === 'non';

            return (
              <tr key={idx} style={{
                backgroundColor: isNon ? '#d32f2f' : idx % 2 === 1 ? '#edeff2' : 'white',
                color: isNon ? 'white' : 'black'
              }}>
                {headers.map((col, ci) => {
                  let val = row[col] || '';
                  let tdStyle = {
                    border: '2px solid black',
                    padding: '8px',
                    overflowWrap: 'break-word',
                    whiteSpace: 'normal',
                    textAlign: 'center',
                  };

                  if (col === 'Unité') {
                    tdStyle.backgroundColor = '#1565c0';
                    tdStyle.color = 'white';
                    tdStyle.fontWeight = 'bold';
                    tdStyle.width = 115;
                    tdStyle.minWidth = 115;
                    tdStyle.textAlign = 'center';
                  } else if (col === 'DATE') {
                    tdStyle.width = 70;
                    tdStyle.minWidth = 70;
                    tdStyle.textAlign = 'center';
                  } else if (col === 'Plainte') {
                    tdStyle.textAlign = 'left';
                    tdStyle.width = 'auto';
                    tdStyle.minWidth = 220;
                  } else if (col === 'GARAGE') {
                    tdStyle.width = 120;
                    tdStyle.minWidth = 80;
                    tdStyle.maxWidth = 150;
                  } else if (col === 'Apte à rouler') {
                    tdStyle.width = 60;
                    tdStyle.minWidth = 60;
                    tdStyle.maxWidth = 60;
                    tdStyle.textAlign = 'center';
                    if (val.toLowerCase() === 'oui' && !isNon) {
                      tdStyle.backgroundColor = '#43a047';
                      tdStyle.color = 'white';
                      tdStyle.fontWeight = 'bold';
                      tdStyle.borderRadius = '3px';
                      tdStyle.letterSpacing = '1px';
                    }
                  }

                  if (col === aptColKey && val.toLowerCase() === 'oui' && !isNon) {
                    val = val.trim();
                  }

                  if (col === 'DATE' && (val.trim() === '' || !val)) val = '-';

                  return (
                    <td key={ci} style={tdStyle}>
                      {val}
                    </td>
                  )
                })}
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
};

export default TruckTable;
