import React, { useEffect, useState } from "react";
import Papa from "papaparse";

function TruckTable() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Remplace "/trucks.csv" par le chemin vers ton CSV
    Papa.parse("/trucks.csv", {
      download: true,
      header: true,
      complete: (results) => {
        setData(results.data);
        setLoading(false);
      },
      error: (err) => {
        console.error("Erreur CSV:", err);
        setLoading(false);
      },
    });
  }, []);

  return (
    <div>
      <h2>Tableau des camions</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Unité</th>
            <th>DATE</th>
            <th>Date Réparation</th>
            <th>Garage</th>
            <th>Complété</th>
            <th>Prochain Rendez-vous</th>
            <th>Disponible</th>
            <th>Apte à rouler</th>
            <th>Plainte</th>
            <th>Note</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan="10">Chargement des données...</td>
            </tr>
          ) : (
            data.map((row, index) => (
              <tr key={index}>
                <td>{row.Unité}</td>
                <td>{row.DATE}</td>
                <td>{row["Date Réparation"]}</td>
                <td>{row.Garage}</td>
                <td>{row.Complété}</td>
                <td>{row["Prochain Rendez-vous"]}</td>
                <td>{row.Disponible}</td>
                <td>{row["Apte à rouler"]}</td>
                <td>{row.Plainte}</td>
                <td>{row.Note}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default TruckTable;
