import React, { useEffect, useMemo, useRef, useState } from "react";
import "./TruckTable.css";

/* ====== Helpers statut ====== */
function toBoolOuiNon(val) {
  const v = (val || "").toString().trim().toLowerCase();
  if (v === "oui" || v === "yes" || v === "true") return true;
  if (v === "non" || v === "no" || v === "false") return false;
  return null;
}

function mapRow(obj) {
  return {
    unite: obj["Unité"]?.trim() || "",
    date: obj["DATE"]?.trim() || "",
    plainte: obj["PLAINTE"]?.trim() || "",
    dateReparation: obj["DATE RÉPARATION"]?.trim() || "",
    garage: obj["GARAGE"]?.trim() || "",
    completee: obj["COMPLÉTÉE"]?.trim() || "",
    prochainRdv:
      obj["PROCHAINE DATE DE RÉPARATION"]?.trim() ||
      obj["PROCHAIN RENDEZ-VOUS"]?.trim() ||
      "",
    disponible: toBoolOuiNon(
      obj["DISPONIBLE"] ?? obj["Disponible"] ?? obj["APTE À ROULER"]
    ),
    note: obj["NOTE"]?.trim() || "",
  };
}

function categorize(unite) {
  const u = (unite || "").trim().toUpperCase();
  if (u.startsWith("RO")) return "ROLL-OFF";
  if (u.startsWith("CA")) return "CHARGEMENT AVANT";
  if (u.startsWith("LA")) return "CHARGEMENT LATÉRAL";
  return "Autres camions";
}

function buildStatusSections(rows) {
  const order = [
    "ROLL-OFF",
    "CHARGEMENT AVANT",
    "CHARGEMENT LATÉRAL",
    "Autres camions",
  ];
  const buckets = Object.fromEntries(order.map((k) => [k, []]));
  for (const r of rows) buckets[categorize(r.unite)].push(r);
  return order.map((name) => ({ name, type: "status", rows: buckets[name] }));
}

/* ====== Création de la section planification ====== */
function buildScheduleSection(scheduleData) {
  return {
    name: scheduleData.title || "Planification de la semaine",
    type: "schedule",
    scheduleData: scheduleData
  };
}

/* ====== Parsing du CSV de planification corrigé ====== */
function parseSheduleText(text) {
  const clean = text.replace(/^\uFEFF/, "");
  const lines = clean.split(/\r?\n/).filter((l) => l.trim().length > 0);

  if (!lines.length) return { title: "", rows: {} };

  // Première ligne = titre semaine complet
  const title = lines[0].replace(/;+$/, "").trim();
  const grouped = {};
  let currentSupplier = "";

  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line || line.match(/^;+$/)) continue; // Ignore les lignes vides ou seulement des points-virgules

    const cells = line.split(';').map((c) => c.trim());
    
    // Si première colonne contient un fournisseur, mettre à jour
    if (cells[0] && cells[0] !== "") {
      currentSupplier = cells[0];
    }

    const jour = cells[1] || "";
    
    // Extraire les unités (on ignore les colonnes "UNITÉ" et ne garde que les vraies unités)
    const units = [];
    for (let j = 2; j < cells.length; j++) {
      if (cells[j] && cells[j] !== "" && cells[j].toUpperCase() !== "UNITÉ") {
        units.push(cells[j].toLowerCase());
      }
    }

    if (currentSupplier && jour && units.length > 0) {
      if (!grouped[currentSupplier]) grouped[currentSupplier] = [];
      grouped[currentSupplier].push({
        supplier: currentSupplier,
        jour,
        units
      });
    }
  }

  return { title, rows: grouped };
}

function formatDateTime(d) {
  try {
    return new Intl.DateTimeFormat("fr-CA", {
      dateStyle: "medium",
      timeStyle: "short",
    }).format(d);
  } catch {
    return d.toLocaleString();
  }
}

/* ====== Vue Statut (inchangée) ====== */
function StatusView({ section }) {
  return (
    <div className="status-section">
      <h3 className="section-title">{section.name}</h3>
      <div className="table-wrapper">
        <table className="truck-table">
          <thead>
            <tr>
              <th>Unité</th>
              <th>DATE</th>
              <th>PLAINTE</th>
              <th>DATE RÉPARATION</th>
              <th>GARAGE</th>
              <th>COMPLÉTÉE</th>
              <th>PROCHAIN RENDEZ-VOUS</th>
              <th>DISPONIBLE</th>
              <th>NOTE</th>
            </tr>
          </thead>
          <tbody>
            {section.rows.map((r, i) => (
              <tr
                key={i}
                className={
                  r.disponible === true
                    ? "status-available"
                    : r.disponible === false
                    ? "status-unavailable"
                    : ""
                }
              >
                <td>{r.unite}</td>
                <td>{r.date}</td>
                <td>{r.plainte}</td>
                <td>{r.dateReparation}</td>
                <td>{r.garage}</td>
                <td>{r.completee}</td>
                <td>{r.prochainRdv}</td>
                <td className="disponible-cell">
                  {r.disponible === null
                    ? ""
                    : r.disponible
                    ? "oui"
                    : "non"}
                </td>
                <td>{r.note}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/* ====== Vue Planning intégrée au slideshow ====== */
function ScheduleView({ section }) {
  const scheduleData = section.scheduleData;
  const suppliers = Object.keys(scheduleData.rows || {});

  if (!suppliers.length) {
    return (
      <div className="status-section">
        <h3 className="section-title">{section.name}</h3>
        <div className="table-wrapper">
          <p>Aucune donnée de planification disponible</p>
        </div>
      </div>
    );
  }

  return (
    <div className="status-section">
      <h3 className="section-title">{section.name}</h3>
      <div className="table-wrapper">
        <table className="truck-table schedule-table">
          <thead>
            <tr>
              <th>Fournisseur</th>
              <th>Jour</th>
              <th>Unités assignées</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.map((supplier) =>
              scheduleData.rows[supplier].map((entry, entryIdx) => (
                <tr key={`${supplier}-${entryIdx}`}>
                  <td className="supplier-cell">
                    {entryIdx === 0 ? supplier : ""}
                  </td>
                  <td className="day-cell">{entry.jour}</td>
                  <td className="units-cell">
                    <div className="units-list">
                      {entry.units.map((unit, unitIdx) => (
                        <span key={unitIdx} className="unit-badge">
                          {unit}
                        </span>
                      ))}
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

/* ====== Composant principal TruckTable ====== */
export default function TruckTable({
  csvUrl = "/camions.csv",
  scheduleUrl = "/shedule.csv",
  refreshInterval = 30000,
}) {
  const [csvData, setCsvData] = useState([]);
  const [scheduleData, setScheduleData] = useState({ title: "", rows: {} });
  const [currentSectionIndex, setCurrentSectionIndex] = useState(0);
  const [lastUpdate, setLastUpdate] = useState(new Date());
  const [error, setError] = useState("");

  const statusSections = useMemo(() => buildStatusSections(csvData), [csvData]);
  const scheduleSection = useMemo(() => buildScheduleSection(scheduleData), [scheduleData]);
  
  // Toutes les sections : statut + planification
  const allSections = useMemo(() => {
    return [...statusSections, scheduleSection];
  }, [statusSections, scheduleSection]);

  const timerRef = useRef();

  // Fonction pour charger les données CSV
  const loadCsvData = async () => {
    try {
      const response = await fetch(csvUrl + "?t=" + Date.now());
      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const text = await response.text();
      const lines = text.split(/\r?\n/).filter((l) => l.trim());
      if (!lines.length) throw new Error("Fichier CSV vide");

      const [header, ...dataLines] = lines;
      const columns = header.split(",").map((c) => c.trim());

      const rows = dataLines.map((line) => {
        const values = line.split(",").map((v) => v.trim());
        const obj = {};
        columns.forEach((col, idx) => {
          obj[col] = values[idx] || "";
        });
        return mapRow(obj);
      });

      setCsvData(rows);
      setError("");
    } catch (err) {
      console.error("Erreur lors du chargement du CSV:", err);
      setError(`Erreur CSV: ${err.message}`);
    }
  };

  // Fonction pour charger les données de planification
  const loadScheduleData = async () => {
    try {
      const response = await fetch(scheduleUrl + "?t=" + Date.now());
      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const text = await response.text();
      const parsed = parseSheduleText(text);
      setScheduleData(parsed);
    } catch (err) {
      console.error("Erreur lors du chargement du planning:", err);
      setScheduleData({ title: "", rows: {} });
    }
  };

  // Fonction pour charger toutes les données
  const loadAllData = async () => {
    await Promise.all([loadCsvData(), loadScheduleData()]);
    setLastUpdate(new Date());
  };

  // Effet pour charger les données au montage et configurer le timer
  useEffect(() => {
    loadAllData();

    if (refreshInterval > 0) {
      timerRef.current = setInterval(loadAllData, refreshInterval);
      return () => clearInterval(timerRef.current);
    }
  }, [csvUrl, scheduleUrl, refreshInterval]);

  // Effet pour l'alternance automatique des sections (slideshow)
  useEffect(() => {
    const slideTimer = setInterval(() => {
      setCurrentSectionIndex((prevIdx) => (prevIdx + 1) % allSections.length);
    }, 10000); // Change de section toutes les 10 secondes

    return () => clearInterval(slideTimer);
  }, [allSections.length]);

  const currentSection = allSections[currentSectionIndex] || { name: "", type: "status", rows: [] };

  return (
    <div className="truck-table-container">
      <header className="table-header">
        <h1>Tableau de Bord - Gestion de Flotte</h1>
        <div className="header-info">
          <span className="last-update">
            Dernière mise à jour: {formatDateTime(lastUpdate)}
          </span>
          <div className="view-indicator">
            Section actuelle: {currentSection.name} ({currentSectionIndex + 1}/{allSections.length})
          </div>
        </div>
        {error && <div className="error-message">{error}</div>}
      </header>

      <main className="table-content">
        {currentSection.type === "status" ? (
          <StatusView section={currentSection} />
        ) : (
          <ScheduleView section={currentSection} />
        )}
      </main>
    </div>
  );
}
