import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types, setTypes] = useState(["RO", "CA", "LA", "Autres"]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  const columnWidths = {
    "Unité": 70,
    "DATE": 50,
    "Date Réparation": 60,
    "Prochain Rendez-vous": 60,
    "Plainte": undefined, // dynamique
    "Garage": 120,
    "Complétée": 40,
    "Disponible": 85,
    "Apte à rouler": 60,
    "Note": undefined,
  };

  // Chargement CSV et traitement
  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const processed = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Autres";
            let type = ["RO", "CA", "LA"].includes(prefix) ? prefix : "Autres";
            return { ...row, Type: type };
          })
          .filter((r) => r["Unité"]); // supprime lignes sans unité
        setData(processed);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const currentType = types[currentTypeIndex];
  const filteredData = data.filter((d) => d.Type === currentType);

  const headers = Object.keys(data[0] || {}).filter((h) => h !== "Type");

  const titleMap = {
    RO: "ROLL-OFF",
    CA: "CHARGEMENT AVANT",
    LA: "CHARGEMENT LATÉRAL",
    Autres: "Autres Camions",
  };

  return (
    <div style={{ width: "100vw", height: "100vh", overflow: "auto", padding: 10 }}>
      {currentType && (
        <h2 style={{ textAlign: "center", marginBottom: 20, fontWeight: "bold", color: "#333" }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: "collapse", width: "100%", tableLayout: "fixed" }}>
        <colgroup>
          {headers.map((h) => {
            const key = h.toLowerCase();
            const w = columnWidths[h] || 100;
            return <col key={h} style={w ? { width: w, minWidth: w, maxWidth: w } : {}} />;
          })}
        </colgroup>
        <thead>
          <tr>
            {headers.map((h) => {
              const isDateRep = h.toLowerCase() === "date réparation" || h.toLowerCase()==='datereparation';
              const isProchainRDV = h.toLowerCase() === "prochain rendez-vous" || h.toLowerCase()==='prochainrendezvous';
              return (
                <th
                  key={h}
                  style={{
                    border: "2px solid black",
                    backgroundColor: "#181818",
                    color: "white",
                    padding: isDateRep || isProchainRDV ? "4px 8px" : "6px 8px",
                    textTransform: "uppercase",
                    whiteSpace: "normal",
                    lineHeight: isDateRep || isProchainRDV ? "1.1em" : undefined,
                    verticalAlign: "bottom",
                    textAlign: "center",
                  }}
                >
                  {isDateRep ? (
                    <>
                      Date
                      <br />
                      Réparation
                    </>
                  ) : isProchainRDV ? (
                    <>
                      PROCHAIN
                      <br />
                      RENDEZ-VOUS
                    </>
                  ) : (
                    h
                  )}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, i) => {
            const aptColKey = headers.find((h) => h.toLowerCase().includes("apte à rouler"));
            const isNon = row[aptColKey]?.toLowerCase() === "non";
            return (
              <tr
                key={i}
                style={{
                  backgroundColor: isNon ? "#d32f2f" : i % 2 === 1 ? "#edeff2" : "white",
                  color: isNon ? "white" : "black",
                }}
              >
                {headers.map((col) => {
                  const isDateRep = col.toLowerCase() === "date réparation" || col.toLowerCase()==='datereparation';
                  const isProchainRDV = col.toLowerCase() === "prochain rendez-vous" || col.toLowerCase()==='prochainrendezvous';
                  return (
                    <td
                      key={col}
                      style={{
                        border: "2px solid black",
                        whiteSpace: "normal",
                        wordBreak: isDateRep || isProchainRDV ? "break-word" : "normal",
                        overflowWrap: isDateRep || isProchainRDV ? "break-word" : "normal",
                        overflow: "hidden",
                        textAlign: col === "Plainte" ? "left" : "center",
                        padding: 8,
                        verticalAlign: col.toUpperCase() === "DISPONIBLE" ? "middle" : "top",
                      }}
                    >
                      {row[col]}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
