import React from "react";
import ScheduleView from "./ScheduleView";

export default function TruckTable({ truckRows, scheduleData }) {

  /* ===== Helpers pour TruckTable ===== */
  const mapRow = (row, idx) => {
    const isAlt = idx % 2 !== 0;
    const rowClass = [
      isAlt ? "row-alt" : "",
      row.disponible === "oui" ? "disponible-oui" : "",
      row.disponible === "non" ? "disponible-non" : "",
      row.status === "unavailable" ? "row-unavailable" : "",
    ]
      .filter(Boolean)
      .join(" ");

    return (
      <tr key={idx} className={rowClass}>
        <td className="col-Unité">{row.unite}</td>
        <td className="col-DATE">{row.date}</td>
        <td className="col-Date-Réparation">{row.dateReparation}</td>
        <td className="col-Garage">{row.garage}</td>
        <td className="col-Complétée">{row.completee}</td>
        <td className="col-Prochain-Rendez-vous">{row.prochainRdv}</td>
        <td className="col-Disponible">{row.disponible}</td>
        <td className="col-Plainte">{row.plainte}</td>
        <td className="col-Note">{row.note}</td>
      </tr>
    );
  };

  return (
    <div className="truck-container">
      <h2 className="truck-title">PLANIFICATION ATELIER</h2>

      {/* ===== Tableau Truck ===== */}
      <table className="truck-table">
        <thead>
          <tr>
            <th className="col-Unité">Unité</th>
            <th className="col-DATE">Date</th>
            <th className="col-Date-Réparation">Date Réparation</th>
            <th className="col-Garage">Garage</th>
            <th className="col-Complétée">Complétée</th>
            <th className="col-Prochain-Rendez-vous">Prochain Rdv</th>
            <th className="col-Disponible">Disponible</th>
            <th className="col-Plainte">Plainte</th>
            <th className="col-Note">Note</th>
          </tr>
        </thead>
        <tbody>
          {truckRows.map(mapRow)}
        </tbody>
      </table>

      {/* ===== Footer / Dernière mise à jour ===== */}
      <div className="footer-bar">
        <div className="last-updated">
          Dernière mise à jour: {new Date().toLocaleString()}
        </div>
      </div>

      {/* ===== Planning / ScheduleView ===== */}
      {scheduleData && <ScheduleView parsed={scheduleData} />}
    </div>
  );
}
