import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types] = useState(["RO", "CA", "LA", "Autres"]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  // Définit la largeur en pixels de chaque colonne, contrôle manuel complet
  const columnWidths = {
    "Unité": 90,
    "DATE": 70,
    "Plainte": 250,           // Largeur fixe personnalisée même pour plainte
    "Date Réparation": 80,
    "Garage": 50,
    "Complétée": 50,
    "Prochain Rendez-vous": 120,
    "Disponible": 90,
    "Note": 220,              // Largeur fixe personnalisée pour note
    "Apte à rouler": 70,
  };

  const titleMap = {
    "Date Réparation": <>DATE<br />RÉPARATION</>,
    "Prochain Rendez-vous": <>PROCHAIN<br />RENDEZ-VOUS</>,
  };

  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const filtered = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Autres";
            const type = ["RO", "CA", "LA"].includes(prefix) ? prefix : "Autres";
            return { ...row, Type: type };
          })
          .filter((r) => r["Unité"]);
        setData(filtered);
      },
    });
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const currentType = types[currentTypeIndex];

  const filteredData = data.filter((d) => d.Type === currentType);
  const headers = Object.keys(data[0] || {}).filter((h) => h !== "Type");

  return (
    <div style={{ width: "1880px", height: "2000px", overflow: "auto", padding: 10 }}>
      {currentType && (
        <h2 style={{ textAlign: "center", marginBottom: 30, fontWeight: "bold", color: "#333" }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: "collapse", width: "100%", margin: "0 auto" }}>
        <thead>
          <tr>
            {headers.map((h) => {
              const width = columnWidths[h] || 10;
              const isUnite = h === "Unité";
              const isTwoLineTitle = h === "Date Réparation" || h === "Prochain Rendez-vous";
              return (
                <th
                  key={h}
                  style={{
                    width,
                    minWidth: 60,
                    maxWidth: 60,
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                    whiteSpace: isTwoLineTitle ? "normal" : "nowrap",
                    border: "2px solid black",
                    backgroundColor: isUnite ? "#0057b7" : "#181818",
                    color: "white",
                    padding: "6px 8px",
                    textAlign: "center",
                    fontWeight: isUnite ? "bold" : "normal",
                    lineHeight: isTwoLineTitle ? "1.1em" : undefined,
                  }}
                >
                  {titleMap[h] || h}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers.find((h) => h.toLowerCase().includes("apte à rouler"));
            const isNon = row[aptColKey]?.toLowerCase() === "non";
            return (
              <tr key={idx} style={{ backgroundColor: isNon ? "#d32f2f" : idx % 2 === 1 ? "#edeff2" : "white", color: isNon ? "white" : "black" }}>
                {headers.map((col) => {
                  const width = columnWidths[col] || 100;
                  const isPlainteOrNote = col === "Plainte" || col === "Note";
                  const isDisponible = col === "Disponible";
              return (
  <td
    key={col}
    style={{
      width,
      minWidth: 40,       // ou largeur souhaitée
      maxWidth: 400,      // max largeur autorisée
      whiteSpace:
        col === "Date Réparation" || col === "Prochain Rendez-vous"
          ? "normal"
          : isPlainteOrNote
          ? "normal"
          : "nowrap",
      overflowWrap:
        col === "Date Réparation" || col === "Prochain Rendez-vous"
          ? "break-word"
          : isPlainteOrNote
          ? "break-word"
          : undefined,
      wordBreak:
        col === "Date Réparation" || col === "Prochain Rendez-vous"
          ? "break-word"
          : isPlainteOrNote
          ? "break-word"
          : undefined,
      overflow: "hidden",
      textOverflow: "ellipsis",
      border: "2px solid black",
      textAlign: isPlainteOrNote ? "left" : "center",
      padding: 8,
      verticalAlign: "top",
      backgroundColor: idx % 2 === 1 && col !== "Disponible" ? "#edeff2" : "white",
      color:
        isDisponible
          ? row[col]?.toLowerCase() === "oui"
            ? "green"
            : row[col]?.toLowerCase() === "non"
            ? "red"
            : "black"
          : undefined,
      fontWeight: isDisponible ? "bold" : undefined,
    }}
  >
    {row[col]}
  </td>
);

                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
