import React, { useEffect, useMemo, useRef, useState } from "react";
import "./TruckTable.css";

/* ====== Helpers statut ====== */
function toBoolOuiNon(val) {
  const v = (val || "").toString().trim().toLowerCase();
  if (v === "oui" || v === "yes" || v === "true") return true;
  if (v === "non" || v === "no" || v === "false") return false;
  return null;
}

function mapRow(obj) {
  return {
    unite: obj["Unité"]?.trim() || "",
    date: obj["DATE"]?.trim() || "",
    plainte: obj["PLAINTE"]?.trim() || "",
    dateReparation: obj["DATE RÉPARATION"]?.trim() || "",
    garage: obj["GARAGE"]?.trim() || "",
    completee: obj["COMPLÉTÉE"]?.trim() || "",
    prochainRdv:
      obj["PROCHAINE DATE DE RÉPARATION"]?.trim() ||
      obj["PROCHAIN RENDEZ-VOUS"]?.trim() ||
      "",
    disponible: toBoolOuiNon(
      obj["DISPONIBLE"] ?? obj["Disponible"] ?? obj["APTE À ROULER"]
    ),
    note: obj["NOTE"]?.trim() || "",
  };
}

function categorize(unite) {
  const u = (unite || "").trim().toUpperCase();
  if (u.startsWith("RO")) return "ROLL-OFF";
  if (u.startsWith("CA")) return "CHARGEMENT AVANT";
  if (u.startsWith("LA")) return "CHARGEMENT LATÉRAL";
  return "Autres camions";
}

function buildStatusSections(rows) {
  const order = [
    "ROLL-OFF",
    "CHARGEMENT AVANT",
    "CHARGEMENT LATÉRAL",
    "Autres camions",
  ];
  const buckets = Object.fromEntries(order.map((k) => [k, []]));
  for (const r of rows) buckets[categorize(r.unite)].push(r);
  return order.map((name) => ({ name, type: "status", rows: buckets[name] }));
}

function formatDateTime(d) {
  try {
    return new Intl.DateTimeFormat("fr-CA", {
      dateStyle: "medium",
      timeStyle: "short",
    }).format(d);
  } catch {
    return d.toLocaleString();
  }
}

/* ====== Parsing du shedule.csv (nouvelle mouture) ====== */
function parseSheduleText(text) {
  const clean = text.replace(/^\uFEFF/, "");
  const lines = clean.split(/\r?\n/).filter((l) => l.trim().length > 0);

  if (!lines.length) return { title: "", rows: {} };

  // Première ligne = titre semaine complet
  const title = lines[0].trim();

  const grouped = {};
  let currentSupplier = "";

  for (let i = 1; i < lines.length; i++) {
    const line = lines[i].trim();
    if (!line) continue;

    const cells = line.split(/\t/).map((c) => c.trim());

    const supplier = cells[0] || currentSupplier;
    currentSupplier = supplier || currentSupplier;

    const jour = cells[1] || "";

    const units = cells.slice(2).map((u) => u.toUpperCase()).filter(Boolean);

    if (!grouped[currentSupplier]) grouped[currentSupplier] = [];
    grouped[currentSupplier].push({ supplier: currentSupplier, jour, units });
  }

  return { title, rows: grouped };
}

/* ====== Vue Planning adaptée au CSV fourni ====== */
function ScheduleView({ parsed }) {
  const suppliers = Object.keys(parsed.rows);

  return (
    <div className="schedule-container">
      <div className="schedule-header">{parsed.title || "PLANIFICATION ATELIER"}</div>
      <table className="schedule-table">
        <thead>
          <tr>
            <th style={{ width: 160 }}>Fournisseur</th>
            <th style={{ width: 140 }}>Jour</th>
            <th style={{ width: 60, textAlign: "center" }}>UNITÉ</th>
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier) =>
            parsed.rows[supplier].map((r, idx) => (
              <tr key={`${supplier}-${idx}`}>
                <td>{idx === 0 ? supplier : ""}</td>
                <td>{r.jour}</td>
                <td className="schedule-col-label">UNITÉ</td>
                {r.units.map((u, i) => (
                  <td key={i} className="schedule-col-unit">{u}</td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

/* ====== Composant principal ====== */
export default function TruckTable() {
  const [rows, setRows] = useState([]);
  const [error, setError] = useState("");
  const [lastUpdated, setLastUpdated] = useState(null);
  const [sheduleParsed, setSheduleParsed] = useState({ title: "", rows: {} });
  const [index, setIndex] = useState(0);
  const timerRef = useRef(null);
  const pausedRef = useRef(false);

  // Charge truck_status.csv
  useEffect(() => {
    async function loadStatus() {
      try {
        const res = await fetch("/truck_status.csv", { cache: "no-store" });
        const text = await res.text();
        const lm = res.headers.get("Last-Modified");
        setLastUpdated(lm ? new Date(lm) : new Date());

        const clean = text.replace(/^\uFEFF/, "");
        const lines = clean.split(/\r?\n/).filter((l) => l.trim().length > 0);
        if (!lines.length) return;

        const headers = lines[0].split(";").map((h) => h.trim());
        const data = lines.slice(1).map((line) => {
          const cells = line.split(";");
          const obj = {};
          headers.forEach((h, i) => (obj[h] = (cells[i] ?? "").trim()));
          return obj;
        });
        const mapped = data
          .map(mapRow)
          .filter((r) =>
            Object.values(r).some((v) => (v ?? "").toString().trim() !== "")
          );
        setRows(mapped);
      } catch (e) {
        setError("Impossible de charger le CSV statut");
        console.error(e);
      }
    }
    loadStatus();
  }, []);

  // Charge schedule.csv (nouvelle mouture)
  useEffect(() => {
    async function loadSchedule() {
      try {
        const res = await fetch("/schedule.csv", { cache: "no-store" });
        if (!res.ok) return;
        const text = await res.text();
        const parsed = parseSheduleText(text);
        setSheduleParsed(parsed);
      } catch (e) {
        console.error("Impossible de charger shedule.csv", e);
      }
    }
    loadSchedule();
  }, []);

  const statusSections = useMemo(() => buildStatusSections(rows), [rows]);
  const hasShedule =
    sheduleParsed.title?.trim().length > 0 ||
    Object.keys(sheduleParsed.rows).length > 0;

  const sections = useMemo(() => {
    const s = [...statusSections];
    if (hasShedule) s.push({ name: "Planification atelier", type: "schedule" });
    return s;
  }, [statusSections, hasShedule]);

  const current = sections[index] || sections[0] || { type: "status", rows: [] };

  useEffect(() => {
    if (!sections.length) return;
    clearInterval(timerRef.current);
    if (!pausedRef.current) {
      timerRef.current = setInterval(
        () => setIndex((s) => (s + 1) % sections.length),
        15000
      );
    }
    return () => clearInterval(timerRef.current);
  }, [sections.length]);

  const onMouseEnter = () => {
    pausedRef.current = true;
    clearInterval(timerRef.current);
  };
  const onMouseLeave = () => {
    pausedRef.current = false;
    clearInterval(timerRef.current);
    timerRef.current = setInterval(
      () => setIndex((s) => (s + 1) % sections.length),
      15000
    );
  };

  const prev = () =>
    setIndex((s) => (s === 0 ? Math.max(0, sections.length - 1) : s - 1));
  const next = () => setIndex((s) => (s + 1) % sections.length);

  return (
    <div className="truck-container" onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave}>
      <div className="slider-bar">
        <button onClick={prev} disabled={!sections.length}>Précédent</button>
        <h2 className="truck-title">{current.name || "Tableau"}</h2>
        <button onClick={next} disabled={!sections.length}>Suivant</button>
        <div className="slider-info">Défilement auto: 15s</div>
      </div>

      {current.type === "schedule" ? (
        <ScheduleView parsed={sheduleParsed} />
      ) : (
        <table className="truck-table">
          <thead>
            <tr>
              <th className="col-Unité">Unité</th>
              <th className="col-DATE">DATE</th>
              <th className="col-Plainte">PLAINTE</th>
              <th className="col-Date-Réparation">DATE RÉPARATION</th>
              <th className="col-Garage">GARAGE</th>
              <th className="col-Complétée">COMPLÉTÉE</th>
              <th className="col-Prochain-Rendez-vous">PROCHAIN RENDEZ-VOUS</th>
              <th className="col-Disponible">DISPONIBLE</th>
              <th className="col-Note">NOTE</th>
            </tr>
          </thead>
          <tbody>
            {current.rows.map((r, idx) => {
              const baseAlt = idx % 2 ? "row-alt" : "";
              const rowUnavailable = r.disponible === false ? "row-unavailable" : "";
              const rowClass = `${baseAlt} ${rowUnavailable}`.trim();

              const dispoClass =
                r.disponible === null
                  ? ""
                  : r.disponible
                  ? "disponible-oui"
                  : "disponible-non";

              return (
                <tr key={`${r.unite}-${idx}`} className={rowClass}>
                  <td className="col-Unité">{r.unite}</td>
                  <td className="col-DATE">{r.date}</td>
                  <td className="col-Plainte">{r.plainte}</td>
                  <td className="col-Date-Réparation">{r.dateReparation}</td>
                  <td className="col-Garage">{r.garage}</td>
                  <td className="col-Complétée">{r.completee}</td>
                  <td className="col-Prochain-Rendez-vous">{r.prochainRdv}</td>
                  <td className={`col-Disponible ${dispoClass}`}>
                    {r.disponible === null ? "" : r.disponible ? "oui" : "non"}
                  </td>
                  <td className="col-Note">{r.note}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      )}

      {lastUpdated && (
        <div className="footer-bar">
          <div className="last-updated">
            Dernière mise à jour: {formatDateTime(lastUpdated)}
          </div>
        </div>
      )}

      {sections.length > 0 && (
        <div className="pager">
          {index + 1} / {sections.length}
        </div>
      )}
    </div>
  );
}
