import React, { useEffect, useMemo, useRef, useState } from "react";
import "./TruckTable.css";

// Parseur pour shedule.csv avec format: Fournisseur;Jour;UNITÉ;val;UNITÉ;val;...
function parseSheduleText(text) {
  const clean = (text ?? "").replace(/\r/g, "").replace(/^\uFEFF/, ""); // Enlève BOM
  const lines = clean.split("\n").filter(l => l.trim().length > 0);
  if (lines.length === 0) return { title: "", rows: {} };

  // Première ligne = titre
  const firstCells = lines[0].split(";");
  const title = firstCells[0] || "PLANIFICATION ATELIER";

  const dataLines = lines.slice(1);
  const flatRows = [];
  let currentSupplier = "";

  for (const raw of dataLines) {
    const cells = raw.split(";").map(c => c.trim());
    
    // Ignorer les lignes vides (tous les champs vides)
    if (cells.every(c => !c)) continue;

    // Fournisseur (colonne 0) - garder le précédent si vide
    const supplier = cells[0] || currentSupplier || "";
    if (cells[0]) currentSupplier = cells[0];
    
    // Jour (colonne 1)
    const jour = cells[1] || "";

    // À partir de la colonne 2, on cherche les paires UNITÉ;valeur
    const unitValues = [];
    for (let i = 2; i < cells.length; i += 2) {
      const label = cells[i] || "";
      const val = cells[i + 1] || "";
      
      if (label.toUpperCase().includes("UNITÉ") || label.toUpperCase().includes("UNITE")) {
        if (val) {
          unitValues.push(val);
        }
      }
    }

    // Ajouter la ligne seulement si elle a un jour ou des unités
    if (jour || unitValues.length > 0) {
      flatRows.push({
        supplier: supplier,
        jour: jour,
        units: unitValues,
      });
    }
  }

  // Grouper par fournisseur en préservant l'ordre
  const grouped = {};
  for (const r of flatRows) {
    const key = r.supplier || "(Inconnu)";
    if (!grouped[key]) grouped[key] = [];
    grouped[key].push(r);
  }

  return { title, rows: grouped };
}

function ScheduleView({ scheduleText }) {
  const parsed = useMemo(() => parseSheduleText(scheduleText), [scheduleText]);
  const suppliers = Object.keys(parsed.rows);

  // Calculer le nombre maximum d'unités pour définir les colonnes
  let maxUnits = 0;
  for (const s of suppliers) {
    for (const r of parsed.rows[s]) {
      if ((r.units?.length || 0) > maxUnits) {
        maxUnits = r.units.length;
      }
    }
  }

  return (
    <div className="schedule-container">
      <div className="schedule-header">{parsed.title}</div>
      <table className="schedule-table">
        <thead>
          <tr>
            <th style={{ width: 160 }}>Fournisseur</th>
            <th style={{ width: 140 }}>Jour</th>
            <th style={{ width: 80 }}>UNITÉ</th>
            {Array.from({ length: maxUnits }, (_, i) => (
              <th key={i} style={{ width: 120 }}>Colonne {i + 1}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {suppliers.map(supplier =>
            parsed.rows[supplier].map((r, idx) => (
              <tr key={`${supplier}-${idx}`}>
                <td>{idx === 0 ? supplier : ""}</td>
                <td>{r.jour}</td>
                <td>UNITÉ</td>
                {Array.from({ length: maxUnits }).map((_, i) => (
                  <td key={i}>
                    {r.units[i] || ""}
                  </td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

// Composant principal
export default function TruckTable() {
  const [scheduleText, setScheduleText] = useState("");

  useEffect(() => {
    // Charger le fichier shedule.csv
    fetch("shedule.csv")
      .then(r => r.text())
      .then(text => {
        console.log("CSV chargé:", text); // Debug
        setScheduleText(text);
      })
      .catch(err => {
        console.error("Erreur chargement CSV:", err);
        setScheduleText("");
      });
  }, []);

  return (
    <div className="truck-container">
      <ScheduleView scheduleText={scheduleText} />
    </div>
  );
}
