import React, { useEffect, useState } from "react";
import Papa from "papaparse";

const TruckTable = () => {
  const [data, setData] = useState([]);
  const [types] = useState(["RO", "CA", "LA", "Autres"]);
  const [currentTypeIndex, setCurrentTypeIndex] = useState(0);

  // Largeurs fixes en px, les colonnes dynamiques n'ont pas de largeur
  const columnWidths = {
    "Unité": 75,
    "DATE": 80,
    "Date Réparation": 100,
    "Garage": 60,
    "Complétée": 60,
    "Prochain Rendez-vous": 110,
    "Disponible": 90,
  };

  const titleMap = {
    "Date Réparation": <>DATE<br />RÉPARATION</>,
    "Prochain Rendez-vous": <>PROCHAIN<br />RENDEZ-VOUS</>,
  };

  useEffect(() => {
    Papa.parse("/truck_status.csv", {
      download: true,
      header: true,
      complete: (results) => {
        // Filtrer et enrichir avec type pour le tri
        const filtered = results.data
          .map((row) => {
            const prefix = row["Unité"]?.substring(0, 2).toUpperCase() || "Autres";
            const type = ["RO", "CA", "LA"].includes(prefix) ? prefix : "Autres";
            return { ...row, Type: type };
          })
          .filter((r) => r["Unité"]);
        setData(filtered);
      },
    });
  }, []);

  useEffect(() => {
    // Cycle automatique type de camion
    const interval = setInterval(() => {
      setCurrentTypeIndex((prev) => (prev + 1) % types.length);
    }, 10000);
    return () => clearInterval(interval);
  }, [types]);

  const currentType = types[currentTypeIndex];
  // Filtrer les données selon le type courant
  const filteredData = data.filter((d) => d.Type === currentType);

  const headers = Object.keys(data[0] || {}).filter((h) => h !== "Type");

  // Colonnes à largeur fixe sauf "Plainte" et "Note" (dynamiques)
  const fixedColumns = Object.keys(columnWidths);

  return (
    <div style={{ width: "100vw", height: "100vh", overflow: "auto", padding: 10 }}>
      {currentType && (
        <h2 style={{ textAlign: "center", marginBottom: 20, fontWeight: "bold", color: "#333" }}>
          {titleMap[currentType] || currentType}
        </h2>
      )}
      <table style={{ borderCollapse: "collapse", width: "100%", tableLayout: "fixed", margin: "0 auto" }}>
        <colgroup>
          {headers.map((h) => {
            if (fixedColumns.includes(h)) {
              const w = columnWidths[h];
              return <col key={h} style={{ width: w, minWidth: w, maxWidth: w }} />;
            }
            // Colonnes dynamiques (Plainte, Note)
            return <col key={h} />;
          })}
        </colgroup>
        <thead>
          <tr>
            {headers.map((h) => {
              const isFixed = fixedColumns.includes(h);
              const width = isFixed ? columnWidths[h] : undefined;
              return (
                <th
                  key={h}
                  style={{
                    width,
                    minWidth: width,
                    maxWidth: width,
                    overflow: "hidden",
                    textOverflow: "ellipsis",
                    whiteSpace: h === "Date Réparation" || h === "Prochain Rendez-vous" ? "normal" : "nowrap",
                    border: "2px solid black",
                    backgroundColor: h === "Unité" ? "#0057b7" : "#181818",
                    color: h === "Unité" ? "white" : "white",
                    padding: "6px 8px",
                    textAlign: "center",
                    fontWeight: h === "Unité" ? "bold" : "normal",
                    lineHeight: h === "Date Réparation" || h === "Prochain Rendez-vous" ? "1.2em" : undefined,
                  }}
                >
                  {titleMap[h] || h}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row, idx) => {
            const aptColKey = headers.find((h) => h.toLowerCase().includes("apte à rouler"));
            const isNon = row[aptColKey]?.toLowerCase() === "non";
            return (
              <tr
                key={idx}
                style={{
                  backgroundColor: isNon ? "#d32f2f" : idx % 2 === 1 ? "#edeff2" : "white",
                  color: isNon ? "white" : "black",
                }}
              >
                {headers.map((col) => {
                  const isFixed = fixedColumns.includes(col);
                  const width = isFixed ? columnWidths[col] : undefined;
                  return (
                    <td
                      key={col}
                      style={{
                        width,
                        minWidth: width,
                        maxWidth: width,
                        whiteSpace: col === "Plainte" || col === "Note" ? "normal" : "nowrap",
                        overflowWrap: col === "Plainte" || col === "Note" ? "break-word" : undefined,
                        wordBreak: col === "Plainte" || col === "Note" ? "break-word" : undefined,
                        overflow: col !== "Plainte" && col !== "Note" ? "hidden" : undefined,
                        textOverflow: col !== "Plainte" && col !== "Note" ? "ellipsis" : undefined,
                        border: "2px solid black",
                        textAlign: col === "Plainte" || col === "Note" ? "left" : "center",
                        padding: 8,
                        verticalAlign: "top",
                        backgroundColor: idx % 2 === 1 && col !== "Disponible" ? "#edeff2" : "white",
                      }}
                    >
                      {col === "Disponible" && row[col]?.toLowerCase() === "oui" ? (
                        <span style={{ color: "green", fontWeight: "bold" }}>oui</span>
                      ) : col === "Disponible" && row[col]?.toLowerCase() === "non" ? (
                        <span style={{ color: "red", fontWeight: "bold" }}>non</span>
                      ) : (
                        row[col]
                      )}
                    </td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default TruckTable;
