import React, { useEffect, useState } from "react";
import Papa from "papaparse"; // Pour parser le CSV

const TruckTable = ({ csvFile }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Lire le CSV
    Papa.parse(csvFile, {
      header: true, // On considère que la première ligne contient les titres
      skipEmptyLines: true,
      complete: (results) => {
        const rows = results.data;
        let lastFournisseur = "";
        const formattedRows = rows.map((row) => {
          // Si la cellule Fournisseur est vide, on utilise le dernier fournisseur connu
          if (row.Fournisseur && row.Fournisseur.trim() !== "") {
            lastFournisseur = row.Fournisseur;
          } else {
            row.Fournisseur = lastFournisseur;
          }
          return row;
        });
        setData(formattedRows);
      },
    });
  }, [csvFile]);

  return (
    <table style={{ borderCollapse: "collapse", width: "100%" }}>
      <thead>
        <tr>
          <th style={{ border: "1px solid black", padding: "8px" }}>Fournisseur</th>
          <th style={{ border: "1px solid black", padding: "8px" }}>Jour</th>
          <th style={{ border: "1px solid black", padding: "8px" }}>Unité</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row, index) => (
          <tr key={index}>
            <td style={{ border: "1px solid black", padding: "8px" }}>{row.Fournisseur}</td>
            <td style={{ border: "1px solid black", padding: "8px" }}>{row.Jour}</td>
            <td style={{ border: "1px solid black", padding: "8px" }}>{row.Unité}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TruckTable;
