import React, { useEffect, useRef } from "react";
import { getDayClass, isCurrentDay } from "./utils";
import "./ScheduleView.css";

export default function ScheduleView({ parsed }) {
  const containerRef = useRef(null);

  useEffect(() => {
    if (!containerRef.current) return;

    let scrollPos = 0;
    const scrollStep = 2;      // pixels par frame, augmente si besoin
    const pause = 5000;        // pause en bas
    let rafId;

    const scroll = () => {
      if (!containerRef.current) return;

      const maxScroll = containerRef.current.scrollHeight - containerRef.current.clientHeight;
      
      // Forcer le scroll même si peu de lignes
      if (maxScroll <= 0) {
        containerRef.current.scrollTop = 0;
        rafId = requestAnimationFrame(scroll);
        return;
      }

      scrollPos += scrollStep;

      if (scrollPos >= maxScroll) {
        scrollPos = 0;
        containerRef.current.scrollTop = 0;
        setTimeout(() => {
          rafId = requestAnimationFrame(scroll);
        }, pause);
        return;
      }

      containerRef.current.scrollTop = scrollPos;
      rafId = requestAnimationFrame(scroll);
    };

    rafId = requestAnimationFrame(scroll);
    return () => cancelAnimationFrame(rafId);
  }, [parsed]);

  const suppliers = Object.keys(parsed.rows);
  const maxUnits = Math.max(
    ...suppliers.flatMap(supplier => parsed.rows[supplier].map(r => r.units.length)),
    1
  );

  return (
    <div
      className="schedule-container"
      ref={containerRef}
      style={{ height: "100%", overflow: "hidden", display: "flex", flexDirection: "column" }}
    >
      <table className="schedule-table">
        <thead>
          <tr>
            <th colSpan={maxUnits + 2} className="schedule-table-title">
              {parsed.title || "PLANIFICATION ATELIER"}
            </th>
          </tr>
          <tr>
            <th>Fournisseur</th>
            <th>Jour</th>
            {Array.from({ length: maxUnits }, (_, i) => (
              <th key={i}>Unité {i + 1}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {suppliers.map((supplier, supplierIdx) =>
            parsed.rows[supplier].map((r, idx) => {
              const isFirstRow = idx === 0;
              const dayClass = getDayClass(r.jour);
              const isEvenSupplier = supplierIdx % 2 === 0;
              const isTodayRow = isCurrentDay(r.jour);

              return (
                <tr
                  key={`${supplier}-${idx}`}
                  className={`
                    ${isFirstRow ? "supplier-first-row" : ""}
                    ${isEvenSupplier ? "supplier-group-even" : ""}
                    ${isTodayRow ? "current-day-row" : ""}
                  `.trim()}
                >
                  <td>{isFirstRow ? supplier : ""}</td>
                  <td className={dayClass}>{r.jour}</td>
                  {Array.from({ length: maxUnits }, (_, i) => (
                    <td key={i}>{r.units[i] || ""}</td>
                  ))}
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
}
